import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../context/AuthContext';
import Sidebar from '../components/Sidebar';
import { Menu, LogOut, TrendingUp, Loader, AlertCircle, CheckCircle, Clock, X } from 'lucide-react';
import apiClient from '../config/api';
import toast from 'react-hot-toast';
import '../styles/Forecasting.css';
import '../styles/Sidebar.css';
import '../styles/GlobalHeader.css';

const Forecasting = () => {
  const { user, logout } = useAuth();
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [loading, setLoading] = useState(false);
  const [projectTypes, setProjectTypes] = useState([]);
  const [formData, setFormData] = useState({
    projectType: '',
    contributorCount: ''
  });
  const [activeJobs, setActiveJobs] = useState([]);
  const [completedForecasts, setCompletedForecasts] = useState([]);
  const [showNotificationModal, setShowNotificationModal] = useState(false);

  useEffect(() => {
    fetchProjectTypes();
    fetchActiveJobs();
    fetchCompletedForecasts();
  }, []);

  // Poll for job status updates
  useEffect(() => {
    if (activeJobs.length === 0) return;

    const pollInterval = setInterval(async () => {
      await checkJobStatuses();
    }, 2000); // Poll every 2 seconds for faster updates

    return () => clearInterval(pollInterval);
  }, [activeJobs.length]);

  const fetchProjectTypes = async () => {
    try {
      const response = await apiClient.get('/forecasting/project-types');
      if (response.data.success) {
        setProjectTypes(response.data.projectTypes || []);
      }
    } catch (error) {
      console.error('Error fetching project types:', error);
      toast.error('Failed to fetch project types');
    }
  };

  const fetchActiveJobs = async () => {
    try {
      // Add timestamp to prevent caching
      const response = await apiClient.get(`/forecasting/jobs?t=${Date.now()}`);
      if (response.data.success) {
        const jobs = response.data.jobs || [];
        setActiveJobs(jobs.filter(job => job.status === 'pending' || job.status === 'processing'));
      }
    } catch (error) {
      console.error('Error fetching active jobs:', error);
    }
  };

  const fetchCompletedForecasts = async () => {
    try {
      // Add timestamp to prevent caching
      const response = await apiClient.get(`/forecasting/completed?t=${Date.now()}`);
      if (response.data.success) {
        setCompletedForecasts(response.data.forecasts || []);
      }
    } catch (error) {
      console.error('Error fetching completed forecasts:', error);
    }
  };

  const checkJobStatuses = async () => {
    try {
      // Add timestamp to prevent caching
      const response = await apiClient.get(`/forecasting/jobs?t=${Date.now()}`);
      if (response.data.success) {
        const allJobs = response.data.jobs || [];
        const newActiveJobs = allJobs.filter(job => job.status === 'pending' || job.status === 'processing');
        const completedJobs = allJobs.filter(job => job.status === 'completed' || job.status === 'failed');

        // Check for newly completed jobs
        const previousJobIds = new Set(activeJobs.map(j => j.id));
        const newlyCompleted = completedJobs.filter(job => previousJobIds.has(job.id));

        if (newlyCompleted.length > 0) {
          newlyCompleted.forEach(job => {
            if (job.status === 'completed') {
              showNotification('Forecast Ready', `Your forecast for ${job.projectType} is ready!`, 'success');
              toast.success(`Forecast for ${job.projectType} is ready!`);
            } else {
              showNotification('Forecast Failed', `Forecast for ${job.projectType} failed: ${job.error || 'Unknown error'}`, 'error');
              toast.error(`Forecast for ${job.projectType} failed: ${job.error || 'Unknown error'}`);
            }
          });
          await fetchCompletedForecasts();
        }

        setActiveJobs(newActiveJobs);
      }
    } catch (error) {
      console.error('Error checking job statuses:', error);
      // Don't show error toast for polling errors to avoid spam
    }
  };

  const showNotification = (title, message, type = 'info') => {
    if ('Notification' in window && Notification.permission === 'granted') {
      new Notification(title, {
        body: message,
        icon: type === 'success' ? '/check-circle.png' : '/alert-circle.png'
      });
    }
  };

  const requestNotificationPermission = async () => {
    if ('Notification' in window) {
      if (Notification.permission === 'default') {
        // Show application modal instead of browser popup
        setShowNotificationModal(true);
      } else if (Notification.permission === 'granted') {
        toast.success('Notifications are already enabled');
      } else {
        toast.info('Notifications are blocked. Please enable them in your browser settings.');
      }
    } else {
      toast.info('Your browser does not support notifications');
    }
  };

  const handleEnableNotifications = async () => {
    setShowNotificationModal(false);
    try {
      const permission = await Notification.requestPermission();
      if (permission === 'granted') {
        toast.success('Notifications enabled successfully!');
      } else if (permission === 'denied') {
        toast.error('Notification permission denied. You can enable it in your browser settings.');
      }
    } catch (error) {
      console.error('Error requesting notification permission:', error);
      toast.error('Failed to enable notifications');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.projectType || !formData.contributorCount) {
      toast.error('Please fill in all fields');
      return;
    }

    const contributorCount = parseInt(formData.contributorCount);
    if (isNaN(contributorCount) || contributorCount <= 0) {
      toast.error('Contributor count must be a positive number');
      return;
    }

    setLoading(true);
    try {
      const response = await apiClient.post('/forecasting/create', {
        projectType: formData.projectType,
        contributorCount: contributorCount
      });

      if (response.data.success) {
        toast.success('Forecast job created! You will be notified when it\'s ready.');
        setFormData({ projectType: '', contributorCount: '' });
        await fetchActiveJobs();
        // Check if notification permission is needed (show modal, don't trigger browser popup)
        if ('Notification' in window && Notification.permission === 'default') {
          setShowNotificationModal(true);
        }
      } else {
        toast.error(response.data.error || 'Failed to create forecast');
      }
    } catch (error) {
      const errorMessage = error.response?.data?.error || error.message || 'Failed to create forecast';
      toast.error(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };

  const formatDuration = (days) => {
    if (days < 1) {
      return `${Math.round(days * 24)} hours`;
    } else if (days < 7) {
      return `${Math.round(days)} day${Math.round(days) !== 1 ? 's' : ''}`;
    } else if (days < 30) {
      const weeks = Math.round(days / 7);
      return `${weeks} week${weeks !== 1 ? 's' : ''}`;
    } else {
      const months = Math.round(days / 30);
      return `${months} month${months !== 1 ? 's' : ''}`;
    }
  };

  const handleCancelJob = async (jobId) => {
    if (!window.confirm('Are you sure you want to cancel this forecast job?')) {
      return;
    }

    try {
      const response = await apiClient.post(`/forecasting/cancel/${jobId}`);
      if (response.data.success) {
        toast.success('Forecast job cancelled successfully');
        await fetchActiveJobs();
      } else {
        toast.error(response.data.error || 'Failed to cancel forecast job');
      }
    } catch (error) {
      const errorMessage = error.response?.data?.error || error.message || 'Failed to cancel forecast job';
      toast.error(errorMessage);
    }
  };

  return (
    <div className="dashboard-layout">
      <Sidebar isOpen={sidebarOpen} toggleSidebar={() => setSidebarOpen(!sidebarOpen)} />
      <div style={{ marginLeft: sidebarOpen ? '320px' : '80px', transition: 'margin-left 0.3s ease', width: sidebarOpen ? 'calc(100% - 320px)' : 'calc(100% - 80px)' }}>
        <div className="forecasting-container">
          <div className="forecasting-header">
            <div className="header-content">
              <div className="header-left">
                <button 
                  className="header-menu-toggle"
                  onClick={() => setSidebarOpen(!sidebarOpen)}
                  aria-label="Toggle sidebar"
                >
                  <Menu size={20} />
                </button>
                <div>
                  <h1 className="page-title">Forecasting</h1>
                  <p className="page-subtitle">Predict project outcomes based on historical data</p>
                </div>
              </div>
              <div className="header-user-profile">
                <div className="user-profile">
                  <div className="user-avatar">
                    {(user?.email || 'U').charAt(0).toUpperCase()}
                  </div>
                  <span className="user-name">{user?.email || 'User'}</span>
                  <button className="logout-btn" onClick={logout} title="Logout">
                    <LogOut size={18} />
                  </button>
                </div>
              </div>
            </div>
          </div>

          <div className="forecasting-content">
            <div className="forecasting-main-content">
              {/* Create Forecast Form */}
              <div className="forecast-form-card">
                <h2 className="card-title">Create New Forecast</h2>
                <form onSubmit={handleSubmit} className="forecast-form">
                  <div className="form-group">
                    <label htmlFor="projectType" className="form-label">
                      Project Type <span className="required">*</span>
                    </label>
                    <select
                      id="projectType"
                      value={formData.projectType}
                      onChange={(e) => setFormData({ ...formData, projectType: e.target.value })}
                      className="form-select"
                      required
                    >
                      <option value="">Select a project type</option>
                      {projectTypes.map((type) => (
                        <option key={type} value={type}>
                          {type}
                        </option>
                      ))}
                    </select>
                  </div>

                  <div className="form-group">
                    <label htmlFor="contributorCount" className="form-label">
                      Number of Contributors <span className="required">*</span>
                    </label>
                    <input
                      type="number"
                      id="contributorCount"
                      value={formData.contributorCount}
                      onChange={(e) => setFormData({ ...formData, contributorCount: e.target.value })}
                      className="form-input"
                      min="1"
                      required
                      placeholder="Enter number of contributors"
                    />
                  </div>

                  <button
                    type="submit"
                    disabled={loading}
                    className="btn-submit"
                  >
                    {loading ? (
                      <>
                        <Loader size={16} className="spinning" />
                        <span>Creating Forecast...</span>
                      </>
                    ) : (
                      <>
                        <TrendingUp size={16} />
                        <span>Create Forecast</span>
                      </>
                    )}
                  </button>
                </form>
              </div>

              {/* Active Jobs */}
              {activeJobs.length > 0 && (
                <div className="forecast-card">
                  <h2 className="card-title">Active Forecasts</h2>
                  <div className="jobs-list">
                    {activeJobs.map((job) => (
                      <div key={job.id} className="job-item processing">
                        <div className="job-icon">
                          <Loader size={20} className="spinning" />
                        </div>
                        <div className="job-details">
                          <div className="job-title">{job.projectType}</div>
                          <div className="job-meta">
                            Contributors: {job.contributorCount} | Status: {job.status}
                            {job.startedAt && (
                              <span> | Started: {new Date(job.startedAt).toLocaleTimeString()}</span>
                            )}
                          </div>
                        </div>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                          <div className="job-status">
                            <Clock size={16} />
                            <span>Processing...</span>
                          </div>
                          <button
                            onClick={() => handleCancelJob(job.id)}
                            className="btn-cancel-job"
                            title="Cancel this forecast job"
                          >
                            <X size={16} />
                            <span>Cancel</span>
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Completed Forecasts */}
              {completedForecasts.length > 0 && (
                <div className="forecast-card">
                  <h2 className="card-title">Completed Forecasts</h2>
                  <div className="forecasts-list">
                    {completedForecasts.map((forecast) => (
                      <div key={forecast.id} className="forecast-item">
                        <div className="forecast-header-item">
                          <div className="forecast-icon">
                            {forecast.status === 'completed' ? (
                              <CheckCircle size={24} className="success-icon" />
                            ) : (
                              <AlertCircle size={24} className="error-icon" />
                            )}
                          </div>
                          <div className="forecast-title-section">
                            <div className="forecast-title">{forecast.projectType}</div>
                            <div className="forecast-meta">
                              {forecast.contributorCount} contributors | Created: {new Date(forecast.createdAt).toLocaleString()}
                            </div>
                          </div>
                        </div>
                        {forecast.status === 'completed' && forecast.predictions && (
                          <div className="forecast-results">
                            <div className="prediction-row">
                              <div className="prediction-label">Estimated Duration:</div>
                              <div className="prediction-value">
                                {formatDuration(forecast.predictions.estimatedDuration)}
                              </div>
                            </div>
                            <div className="prediction-row">
                              <div className="prediction-label">Estimated Budget:</div>
                              <div className="prediction-value">
                                {formatCurrency(forecast.predictions.estimatedBudget)}
                              </div>
                            </div>
                            <div className="prediction-row">
                              <div className="prediction-label">Success Probability:</div>
                              <div className="prediction-value">
                                {Math.round(forecast.predictions.successProbability * 100)}%
                              </div>
                            </div>
                            {forecast.predictions.confidence && (
                              <div className="prediction-row">
                                <div className="prediction-label">Confidence Level:</div>
                                <div className="prediction-value">
                                  {Math.round(forecast.predictions.confidence * 100)}%
                                </div>
                              </div>
                            )}
                            {forecast.predictions.notes && (
                              <div className="prediction-notes">
                                <strong>Notes:</strong> {forecast.predictions.notes}
                              </div>
                            )}
                          </div>
                        )}
                        {forecast.status === 'failed' && (
                          <div className="forecast-error">
                            <AlertCircle size={16} />
                            <span>{forecast.error || 'Forecast generation failed'}</span>
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {activeJobs.length === 0 && completedForecasts.length === 0 && (
                <div className="empty-state">
                  <TrendingUp size={48} className="empty-icon" />
                  <h3>No Forecasts Yet</h3>
                  <p>Create your first forecast by selecting a project type and number of contributors above.</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Notification Permission Modal */}
      {showNotificationModal && (
        <div className="modal-overlay" onClick={() => setShowNotificationModal(false)}>
          <div className="notification-modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Enable Notifications</h3>
              <button 
                className="modal-close-btn"
                onClick={() => setShowNotificationModal(false)}
                aria-label="Close"
              >

              </button>
            </div>
            <div className="modal-content">
              <p>
                Would you like to receive browser notifications when your forecasts are ready?
              </p>
              <p className="modal-subtext">
                This will allow the application to notify you when forecast calculations complete, so you don't have to wait on the page.
              </p>
            </div>
            <div className="modal-actions">
              <button
                className="btn-modal-secondary"
                onClick={() => setShowNotificationModal(false)}
              >
                Not Now
              </button>
              <button
                className="btn-modal-primary"
                onClick={handleEnableNotifications}
              >
                Enable Notifications
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Forecasting;
