import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useAuth } from '../../../context/AuthContext';
import Sidebar from '../../../components/Sidebar';
import useSidebarWidth from '../../../hooks/useSidebarWidth';
import { Menu, LogOut, RefreshCw, Loader } from 'lucide-react';
import apiClient from '../../../config/api';
import toast from 'react-hot-toast';
import BookmarkButton from '../../../components/BookmarkButton';
import DeadlinesPanel from './components/DeadlinesPanel';
import RecordsCount from './components/RecordsCount';
import FilterPanel from './components/FilterPanel';
import PendingHoursCard from './components/PendingHoursCard';
import MetricsRow from './components/MetricsRow';
import PendingUnitsCard from './components/PendingUnitsCard';
import ApprovalsTable from './components/ApprovalsTable';
import ActionButtons from './components/ActionButtons';
import ApprovalModal from './components/ApprovalModal';
import RejectModal from './components/RejectModal';
import EmailModal from './components/EmailModal';
import './PMApprovals.css';
import '../../../styles/Sidebar.css';
import '../../../styles/GlobalHeader.css';

const PMApprovals = () => {
  const { user, logout } = useAuth();
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const sidebarWidth = useSidebarWidth(sidebarOpen);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);

  // Data states
  const [records, setRecords] = useState([]);
  const [summary, setSummary] = useState(null);
  const [deadlines, setDeadlines] = useState(null);
  const [filterOptions, setFilterOptions] = useState({
    accounts: [],
    projects: [],
    projectObjectives: [],
    emails: [],
    statuses: []
  });

  // Filter states
  const [filters, setFilters] = useState({
    account: 'all',
    project: 'all',
    projectObjective: 'all',
    email: 'all',
    transactionDateFrom: '',
    transactionDateTo: '',
    weekendingDateFrom: '',
    weekendingDateTo: '',
    transactionId: ''
  });

  // Table states
  const [offset, setOffset] = useState(0);
  const [hasMore, setHasMore] = useState(true);
  const [loadingMore, setLoadingMore] = useState(false);
  const [isLoadingMoreBatches, setIsLoadingMoreBatches] = useState(false);
  const [totalRecords, setTotalRecords] = useState(0);
  const [sortBy, setSortBy] = useState('Transaction_Date__c');
  const [sortOrder, setSortOrder] = useState('DESC');
  const [selectedRows, setSelectedRows] = useState([]);

  // Modal states
  const [showApprovalModal, setShowApprovalModal] = useState(false);
  const [showRejectModal, setShowRejectModal] = useState(false);
  const [showEmailModal, setShowEmailModal] = useState(false);

  // Track if warning has been shown to avoid duplicates
  const warningShownRef = useRef(false);

  const tableContainerRef = useRef(null);

  // Fetch deadlines
  const fetchDeadlines = useCallback(async () => {
    try {
      const response = await apiClient.get('/pm-approvals/deadlines');
      if (response.data.success) {
        setDeadlines(response.data.deadlines);
      }
    } catch (error) {
      console.error('Error fetching deadlines:', error);
      // Don't show error toast - backend might be restarting
      // The UI will continue to work with cached/default data
    }
  }, []);

  // Fetch filter options
  const fetchFilterOptions = useCallback(async () => {
    try {
      const response = await apiClient.get('/pm-approvals/filters');
      if (response.data.success) {
        setFilterOptions(response.data.filters);

        // Show warning if Salesforce object is not configured (only once)
        if (response.data.warning && !warningShownRef.current) {
          warningShownRef.current = true;
          toast(response.data.warning, {
            duration: 8000,
            icon: '⚠️',
            style: {
              background: '#fffbeb',
              color: '#92400e',
              border: '1px solid #fde68a'
            }
          });
        }
      }
    } catch (error) {
      console.error('Error fetching filter options:', error);
      // Don't show error toast - backend might be restarting
      // Use empty filter options as fallback
      setFilterOptions({
        accounts: [],
        projects: [],
        projectObjectives: [],
        emails: [],
        statuses: []
      });
    }
  }, []);

  // Fetch summary metrics
  const fetchSummary = useCallback(async (silent = false) => {
    try {
      const params = new URLSearchParams();
      Object.keys(filters).forEach(key => {
        if (filters[key] && filters[key] !== 'all' && filters[key] !== '') {
          params.append(key, filters[key]);
        }
      });

      const response = await apiClient.get(`/pm-approvals/summary?${params.toString()}`);
      if (response.data.success) {
        setSummary(response.data.data);

        // Show warning if Salesforce object is not configured (only once)
        if (response.data.warning && !silent && !warningShownRef.current) {
          warningShownRef.current = true;
          toast(response.data.warning, {
            duration: 8000,
            icon: '⚠️',
            style: {
              background: '#fffbeb',
              color: '#92400e',
              border: '1px solid #fde68a'
            }
          });
        }
      }
    } catch (error) {
      console.error('Error fetching summary:', error);
      // Don't show error toast if backend is down - UI will show loading/empty state
      // Only show error if it's not a connection error
      if (!silent && error.code !== 'ECONNREFUSED' && error.code !== 'ERR_NETWORK' && !error.isBackendDown) {
        toast.error('Failed to load summary metrics');
      }
    }
  }, [filters]);

  // Fetch records in batches of 10,000
  const fetchRecords = useCallback(async (reset = false, customOffset = null) => {
    if (reset) {
      setLoading(true);
      setOffset(0);
      setRecords([]);
      setIsLoadingMoreBatches(true);
    } else {
      setLoadingMore(true);
    }

    try {
      const params = new URLSearchParams();
      const currentOffset = reset ? 0 : (customOffset !== null ? customOffset : offset);
      params.append('offset', currentOffset.toString());
      params.append('limit', '10000'); // Request 10,000 records per batch
      params.append('sortBy', sortBy);
      params.append('sortOrder', sortOrder);

      Object.keys(filters).forEach(key => {
        if (filters[key] && filters[key] !== 'all' && filters[key] !== '') {
          params.append(key, filters[key]);
        }
      });

      const response = await apiClient.get(`/pm-approvals/list?${params.toString()}`);
      if (response.data.success) {
        const newRecords = response.data.records || [];
        const total = response.data.total || 0;
        setTotalRecords(total);

        // Always deduplicate records to prevent duplicate keys
        if (reset) {
          // Deduplicate even on reset (defensive)
          const uniqueRecords = [];
          const seenIds = new Set();
          newRecords.forEach(record => {
            if (record && record.id && !seenIds.has(record.id)) {
              seenIds.add(record.id);
              uniqueRecords.push(record);
            }
          });
          setRecords(uniqueRecords);
          setOffset(uniqueRecords.length);
        } else {
          setRecords(prev => {
            const existingIds = new Set(prev.map(r => r.id));
            const uniqueNewRecords = newRecords.filter(r => r && r.id && !existingIds.has(r.id));
            return [...prev, ...uniqueNewRecords];
          });
          setOffset(prev => prev + newRecords.length);
        }

        const hasMore = response.data.hasMore || false;
        setHasMore(hasMore);

        console.log(`Frontend batch status: hasMore=${hasMore}, newRecords=${newRecords.length}, total=${total}, currentOffset=${currentOffset}`);

        // Automatically load next batch if there are more records
        // Continue loading even if newRecords.length is 0 (might be due to offset > 2000 skipping)
        if (hasMore) {
          // Use setTimeout to allow UI to update before loading next batch
          // This keeps the UI responsive while loading
          setTimeout(() => {
            // Calculate next offset correctly: use effectiveOffset from backend if available, otherwise calculate
            const effectiveOffset = response.data.effectiveOffset !== undefined 
              ? response.data.effectiveOffset 
              : (reset ? newRecords.length : (currentOffset + newRecords.length));
            const nextOffset = effectiveOffset;
            console.log(`Auto-loading next batch: currentOffset=${currentOffset}, newRecords=${newRecords.length}, effectiveOffset=${effectiveOffset}, nextOffset=${nextOffset}, total=${total}, hasMore=${hasMore}`);

            // Only load if we haven't reached the total and we're making progress
            if (nextOffset < total && (newRecords.length > 0 || effectiveOffset > currentOffset)) {
              fetchRecords(false, nextOffset).catch((err) => {
                console.error('Error loading next batch:', err);
                setIsLoadingMoreBatches(false);
              });
            } else {
              console.log('Reached total records or no progress, stopping auto-load');
              setIsLoadingMoreBatches(false);
            }
          }, 100);
        } else {
          console.log('No more batches to load');
          setIsLoadingMoreBatches(false);
        }

        // Show warning if Salesforce object is not configured (only once)
        if (response.data.warning && reset && !warningShownRef.current) {
          warningShownRef.current = true;
          toast(response.data.warning, {
            duration: 8000,
            icon: '⚠️',
            style: {
              background: '#fffbeb',
              color: '#92400e',
              border: '1px solid #fde68a'
            }
          });
        }
      } else {
        setIsLoadingMoreBatches(false);
        toast.error(response.data.error || 'Failed to fetch approvals');
      }
    } catch (error) {
      setIsLoadingMoreBatches(false);
      // Don't show error toast if backend is down - UI will show empty state
      // Only show error if it's not a connection error
      if (error.code !== 'ECONNREFUSED' && error.code !== 'ERR_NETWORK' && !error.isBackendDown) {
        const errorMessage = error.response?.data?.error || error.message || 'Failed to fetch approvals';
        toast.error(errorMessage);
      }
    } finally {
      setLoading(false);
      setLoadingMore(false);
      setRefreshing(false);
    }
  }, [filters, sortBy, sortOrder, offset]);

  // Track if initial load has been done
  const initialLoadDoneRef = useRef(false);

  // Initial load - only runs once on mount
  useEffect(() => {
    if (!initialLoadDoneRef.current) {
      initialLoadDoneRef.current = true;
      fetchDeadlines();
      fetchFilterOptions();
      fetchSummary(true);
      fetchRecords(true);
    }
  }, [fetchDeadlines, fetchFilterOptions, fetchSummary, fetchRecords]);

  // Continue auto-loading if there's more data available
  // This runs when hasMore changes to true and we're not already loading
  useEffect(() => {
    if (hasMore && !loading && !loadingMore && !isLoadingMoreBatches && records.length > 0) {
      // Check if we should continue loading
      const currentOffset = offset;
      if (currentOffset < totalRecords) {
        console.log('Continuing auto-load from effect: offset=', currentOffset, 'total=', totalRecords);
        fetchRecords(false, currentOffset).catch((err) => {
          console.error('Error continuing auto-load:', err);
        });
      }
    }
  }, [hasMore, loading, loadingMore, isLoadingMoreBatches, records.length, offset, totalRecords]); // eslint-disable-line react-hooks/exhaustive-deps

  // Note: Filters and sort changes no longer trigger data reset
  // Data persists during the session and only reloads when refresh button is clicked

  const handleRefresh = useCallback((e) => {
    if (e) {
      e.preventDefault();
      e.stopPropagation();
    }
    setRefreshing(true);
    setOffset(0);
    setRecords([]);
    setHasMore(true);
    setSelectedRows([]);
    setIsLoadingMoreBatches(false);
    setTotalRecords(0);
    // Reset warning shown flag so warning can be shown again if needed
    warningShownRef.current = false;
    fetchRecords(true).finally(() => {
      setRefreshing(false);
    });
    fetchSummary();
    fetchFilterOptions();
  }, [fetchRecords, fetchSummary, fetchFilterOptions]);

  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
  };

  const handleClearFilters = (clearedFilters) => {
    setFilters(clearedFilters);
  };

  const handleSort = (column, order) => {
    setSortBy(column);
    setSortOrder(order);
  };

  const handleRowSelect = (recordId) => {
    setSelectedRows(prev => {
      if (prev.includes(recordId)) {
        return prev.filter(id => id !== recordId);
      } else {
        return [...prev, recordId];
      }
    });
  };

  const handleApprove = () => {
    if (selectedRows.length === 0) {
      toast.error('Please select at least one record to approve');
      return;
    }
    setShowApprovalModal(true);
  };

  const handleReject = () => {
    if (selectedRows.length === 0) {
      toast.error('Please select at least one record to reject');
      return;
    }
    setShowRejectModal(true);
  };

  const handleSendEmail = () => {
    if (selectedRows.length === 0) {
      toast.error('Please select at least one record to email');
      return;
    }
    setShowEmailModal(true);
  };

  const handleApprovalConfirm = async (comment) => {
    try {
      const response = await apiClient.post('/pm-approvals/approve', {
        transactionIds: selectedRows,
        comment: comment || ''
      });

      if (response.data.success) {
        toast.success(`Successfully approved ${response.data.approved} record(s)`);
        setSelectedRows([]);
        setShowApprovalModal(false);
        fetchRecords(true);
        fetchSummary();
      } else {
        toast.error(response.data.error || 'Failed to approve records');
      }
    } catch (error) {
      toast.error(error.response?.data?.error || 'Failed to approve records');
    }
  };

  const handleRejectConfirm = async (reason) => {
    try {
      const response = await apiClient.post('/pm-approvals/reject', {
        transactionIds: selectedRows,
        reason: reason || ''
      });

      if (response.data.success) {
        toast.success(`Successfully rejected ${response.data.rejected} record(s)`);
        setSelectedRows([]);
        setShowRejectModal(false);
        fetchRecords(true);
        fetchSummary();
      } else {
        toast.error(response.data.error || 'Failed to reject records');
      }
    } catch (error) {
      toast.error(error.response?.data?.error || 'Failed to reject records');
    }
  };

  const handleEmailConfirm = async (emailData) => {
    // Email functionality - to be implemented
    toast.info('Email functionality coming soon');
    setShowEmailModal(false);
  };

  return (
    <div className="dashboard-layout">
      <Sidebar isOpen={sidebarOpen} toggleSidebar={() => setSidebarOpen(!sidebarOpen)} />

      <div 
        className="pm-approvals-page" 
        style={{ 
          marginLeft: `${sidebarWidth}px`, 
          width: `calc(100% - ${sidebarWidth}px)`,
          transition: 'margin-left 0.2s ease, width 0.2s ease'
        }}
      >
        <div className="pm-approvals-container">
          {/* Header */}
          <div className="pm-approvals-header">
            <div className="header-content">
              <div className="header-left">
                <button 
                  className="header-menu-toggle"
                  onClick={() => setSidebarOpen(!sidebarOpen)}
                  aria-label="Toggle sidebar"
                >
                  <Menu size={20} />
                </button>
                <div>
                  <h1 className="page-title">PM Approvals for Self Reported Time</h1>
                </div>
              </div>
              <div className="header-right">
                <BookmarkButton />
                <button
                  className="refresh-btn"
                  onClick={handleRefresh}
                  disabled={refreshing}
                  title="Refresh"
                >
                  <RefreshCw size={18} className={refreshing ? 'spinning' : ''} />
                </button>
                <div className="header-user-profile">
                  <div className="user-profile">
                    <div className="user-avatar">
                      {(user?.email || 'U').charAt(0).toUpperCase()}
                    </div>
                    <span className="user-name">{user?.email || 'User'}</span>
                    <button className="logout-btn" onClick={logout} title="Logout">
                      <LogOut size={18} />
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Main Content */}
          <div className="pm-approvals-content">
            {/* Left Sidebar */}
            <div className="pm-approvals-sidebar">
              <DeadlinesPanel deadlines={deadlines} loading={!deadlines} />
              <RecordsCount 
                count={records.length} 
                total={totalRecords}
                loading={loading} 
                isLoadingMore={isLoadingMoreBatches || loadingMore}
              />
              <FilterPanel
                filters={filters}
                onFilterChange={handleFilterChange}
                onClearFilters={handleClearFilters}
                filterOptions={filterOptions}
                loading={loading}
              />
              <ActionButtons
                onApprove={handleApprove}
                onReject={handleReject}
                onSendEmail={handleSendEmail}
                disabled={loading}
                selectedCount={selectedRows.length}
              />
            </div>

            {/* Main Content Area */}
            <div className="pm-approvals-main" ref={tableContainerRef}>
              {/* Summary Metrics */}
              <div className="summary-metrics-section">
                <PendingHoursCard
                  totalPendingHours={summary?.totalPendingHours || 0}
                  totalHours={summary?.totalHours || 0}
                  loading={!summary}
                />
                <MetricsRow
                  selfReportedTime={summary?.selfReportedTime || 0}
                  systemTracked={summary?.systemTracked || 0}
                  payment={summary?.totalPayment || 0}
                  loading={!summary}
                />
                <PendingUnitsCard
                  totalPendingUnits={summary?.totalPendingUnits || 0}
                  loading={!summary}
                />
              </div>

              {/* Data Table */}
              <div className="table-section">
                <ApprovalsTable
                  records={records}
                  loading={loading}
                  onSort={handleSort}
                  sortBy={sortBy}
                  sortOrder={sortOrder}
                  selectedRows={selectedRows}
                  onRowSelect={handleRowSelect}
                />
                {loadingMore && (
                  <div className="loading-more">
                    <Loader className="spinning" size={20} />
                    <span>Loading more records...</span>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Modals */}
      {showApprovalModal && (
        <ApprovalModal
          selectedCount={selectedRows.length}
          onConfirm={handleApprovalConfirm}
          onClose={() => setShowApprovalModal(false)}
        />
      )}
      {showRejectModal && (
        <RejectModal
          selectedCount={selectedRows.length}
          onConfirm={handleRejectConfirm}
          onClose={() => setShowRejectModal(false)}
        />
      )}
      {showEmailModal && (
        <EmailModal
          selectedCount={selectedRows.length}
          selectedRecords={records.filter(r => selectedRows.includes(r.id))}
          onConfirm={handleEmailConfirm}
          onClose={() => setShowEmailModal(false)}
        />
      )}
    </div>
  );
};

export default PMApprovals;
