import React, { useState, useEffect } from 'react';
import { RotateCcw } from 'lucide-react';
import SearchableSelect from './SearchableSelect';
import './FilterPanel.css';

const FilterPanel = ({ 
  filters, 
  onFilterChange, 
  onClearFilters, 
  filterOptions,
  loading 
}) => {
  const [localFilters, setLocalFilters] = useState(filters || {
    account: 'all',
    project: 'all',
    projectObjective: 'all',
    email: 'all',
    transactionDateFrom: '',
    transactionDateTo: '',
    weekendingDateFrom: '',
    weekendingDateTo: '',
    transactionId: ''
  });

  useEffect(() => {
    setLocalFilters(filters || {
      account: 'all',
      project: 'all',
      projectObjective: 'all',
      email: 'all',
      transactionDateFrom: '',
      transactionDateTo: '',
      weekendingDateFrom: '',
      weekendingDateTo: '',
      transactionId: ''
    });
  }, [filters]);

  const handleFilterChange = (key, value) => {
    const newFilters = { ...localFilters, [key]: value };

    // Cascading filters: reset dependent filters
    if (key === 'account') {
      newFilters.project = 'all';
      newFilters.projectObjective = 'all';
    } else if (key === 'project') {
      newFilters.projectObjective = 'all';
    }

    setLocalFilters(newFilters);
    onFilterChange(newFilters);
  };

  const handleClearAll = () => {
    const clearedFilters = {
      account: 'all',
      project: 'all',
      projectObjective: 'all',
      email: 'all',
      transactionDateFrom: '',
      transactionDateTo: '',
      weekendingDateFrom: '',
      weekendingDateTo: '',
      transactionId: ''
    };
    setLocalFilters(clearedFilters);
    onClearFilters(clearedFilters);
  };

  // Utility function to ensure value is an array (handles Set, Map, etc.)
  const ensureArray = (value) => {
    if (Array.isArray(value)) return value;
    if (value && typeof value === 'object') {
      // Handle Set
      if (value.constructor === Set || value instanceof Set) {
        return Array.from(value);
      }
      // Handle Map
      if (value.constructor === Map || value instanceof Map) {
        return Array.from(value.values());
      }
      // Handle array-like objects
      if (value.length !== undefined) {
        return Array.from(value);
      }
    }
    return [];
  };

  // Filter projects by selected account (if needed)
  const filteredProjects = ensureArray(filterOptions?.projects);
  const filteredProjectObjectives = ensureArray(filterOptions?.projectObjectives);
  const accountsArray = ensureArray(filterOptions?.accounts);
  const emailsArray = ensureArray(filterOptions?.emails);

  return (
    <div className="filter-panel">
      <div className="filter-list">
        {/* Account Filter */}
        <div className="filter-row">
          <label className="filter-label">Account</label>
          <div className="filter-dropdown-wrapper">
            <SearchableSelect
              value={localFilters.account || 'all'}
              onChange={(value) => handleFilterChange('account', value)}
              options={accountsArray}
              placeholder="Search accounts..."
              disabled={loading}
              showAllOption={true}
              allOptionLabel="All"
              allOptionValue="all"
            />
          </div>
        </div>

        {/* Project Filter */}
        <div className="filter-row">
          <label className="filter-label">Project</label>
          <div className="filter-dropdown-wrapper">
            <SearchableSelect
              value={localFilters.project || 'all'}
              onChange={(value) => handleFilterChange('project', value)}
              options={filteredProjects}
              placeholder="Search projects..."
              disabled={loading || localFilters.account === 'all'}
              showAllOption={true}
              allOptionLabel="All"
              allOptionValue="all"
            />
          </div>
        </div>

        {/* Project Objective Filter */}
        <div className="filter-row">
          <label className="filter-label">Project Objective</label>
          <div className="filter-dropdown-wrapper">
            <SearchableSelect
              value={localFilters.projectObjective || 'all'}
              onChange={(value) => handleFilterChange('projectObjective', value)}
              options={filteredProjectObjectives}
              placeholder="Search project objectives..."
              disabled={loading || localFilters.project === 'all'}
              showAllOption={true}
              allOptionLabel="All"
              allOptionValue="all"
              getOptionLabel={(opt) => typeof opt === 'object' && opt !== null ? opt.name : opt}
              getOptionValue={(opt) => typeof opt === 'object' && opt !== null ? opt.name : opt}
              getOptionKey={(opt, index) => typeof opt === 'object' && opt !== null ? opt.id : (opt || index)}
            />
          </div>
        </div>

        {/* Email Filter */}
        <div className="filter-row">
          <label className="filter-label">Email</label>
          <div className="filter-dropdown-wrapper">
            <SearchableSelect
              value={localFilters.email || 'all'}
              onChange={(value) => handleFilterChange('email', value)}
              options={emailsArray}
              placeholder="Search emails..."
              disabled={loading}
              showAllOption={true}
              allOptionLabel="All"
              allOptionValue="all"
            />
          </div>
        </div>

        {/* Transaction Date Filter */}
        <div className="filter-row">
          <label className="filter-label">Transaction date</label>
          <div className="filter-date-range">
            <input
              type="date"
              className="filter-date-input"
              value={localFilters.transactionDateFrom || ''}
              onChange={(e) => handleFilterChange('transactionDateFrom', e.target.value)}
              disabled={loading}
            />
            <span className="filter-date-separator">to</span>
            <input
              type="date"
              className="filter-date-input"
              value={localFilters.transactionDateTo || ''}
              onChange={(e) => handleFilterChange('transactionDateTo', e.target.value)}
              disabled={loading}
            />
          </div>
        </div>

        {/* Weekending Date Filter */}
        <div className="filter-row">
          <label className="filter-label">Weekending Date</label>
          <div className="filter-date-range">
            <input
              type="date"
              className="filter-date-input"
              value={localFilters.weekendingDateFrom || ''}
              onChange={(e) => handleFilterChange('weekendingDateFrom', e.target.value)}
              disabled={loading}
            />
            <span className="filter-date-separator">to</span>
            <input
              type="date"
              className="filter-date-input"
              value={localFilters.weekendingDateTo || ''}
              onChange={(e) => handleFilterChange('weekendingDateTo', e.target.value)}
              disabled={loading}
            />
          </div>
        </div>

        {/* Transaction ID Filter */}
        <div className="filter-row">
          <label className="filter-label">Transaction ID</label>
          <input
            type="text"
            className="filter-text-input"
            value={localFilters.transactionId || ''}
            onChange={(e) => handleFilterChange('transactionId', e.target.value)}
            disabled={loading}
            placeholder="Enter Transaction ID"
          />
        </div>
      </div>

      <button className="clear-filter-btn" onClick={handleClearAll}>
        <RotateCcw size={16} />
        Clear Filter
      </button>
    </div>
  );
};

export default FilterPanel;
