            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 1000,
            padding: '12px',
            boxSizing: 'border-box'
          }}
          onClick={() => setShowPublishResultsModal(false)}
        >
          <div 
            style={{
              backgroundColor: '#ffffff',
              borderRadius: '8px',
              width: '90%',
              maxWidth: '700px',
              maxHeight: '90vh',
              display: 'flex',
              flexDirection: 'column',
              boxShadow: '0 4px 20px rgba(0, 0, 0, 0.15)',
              boxSizing: 'border-box',
              color: '#002329',
              fontFamily: 'Poppins',
              fontSize: '14px'
            }}
            onClick={(e) => e.stopPropagation()}
          >
            {/* Modal Header */}
            <div style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'space-between',
              padding: '12px 24px',
              borderBottom: '1px solid #e2e8f0',
              flexShrink: 0
            }}>
              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '12px'
              }}>
                {publishResults.failed.length > 0 ? (
                  <XCircle size={24} color="#ef4444" />
                ) : (
                  <CheckCircle size={24} color="#10b981" />
                )}
                <h2 style={{
                  margin: 0,
                  fontSize: '20px',
                  fontWeight: 600,
                  color: '#002329',
                  fontFamily: 'Poppins'
                }}>
                  Publish Results
                </h2>
              </div>
              <button
                onClick={() => setShowPublishResultsModal(false)}
                style={{
                  background: 'none',
                  border: 'none',
                  cursor: 'pointer',
                  padding: '4px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  borderRadius: '4px',
                  transition: 'background-color 0.2s'
                }}
                onMouseEnter={(e) => e.target.style.backgroundColor = '#f1f5f9'}
                onMouseLeave={(e) => e.target.style.backgroundColor = 'transparent'}
              >
                <X size={20} color="#64748b" />
              </button>
            </div>

            {/* Modal Content */}
            <div style={{
              padding: '24px',
              overflowY: 'auto',
              flex: 1
            }}>
              {/* Published Objects */}
              {publishResults.published.length > 0 && (
                <div style={{ marginBottom: '24px' }}>
                  <h3 style={{
                    margin: '0 0 16px 0',
                    fontSize: '14px',
                    fontWeight: 600,
                    color: '#10b981',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '8px'
                  }}>
                    <CheckCircle size={18} />
                    Published Successfully ({publishResults.published.length})
                  </h3>
                  <div style={{
                    display: 'flex',
                    flexDirection: 'column',
                    gap: '12px'
                  }}>
                    {publishResults.published.map((obj, index) => (
                      <div key={index} style={{
                        padding: '16px',
                        backgroundColor: '#f0fdf4',
                        border: '1px solid #bbf7d0',
                        borderRadius: '8px'
                      }}>
                        <div style={{
                          fontWeight: 600,
                          color: '#002329',
                          marginBottom: '8px'
                        }}>
                          {obj.type}: "{obj.name}"
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Failed Objects */}
              {publishResults.failed.length > 0 && (
                <div>
                  <h3 style={{
                    margin: '0 0 16px 0',
                    fontSize: '14px',
                    fontWeight: 600,
                    color: '#ef4444',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '8px'
                  }}>
                    <XCircle size={18} />
                    Failed to Publish ({publishResults.failed.length})
                  </h3>
                  <div style={{
                    display: 'flex',
                    flexDirection: 'column',
                    gap: '12px'
                  }}>
                    {publishResults.failed.map((obj, index) => (
                      <div key={index} style={{
                        padding: '16px',
                        backgroundColor: '#fef2f2',
                        border: '1px solid #fecaca',
                        borderRadius: '8px'
                      }}>
                        <div style={{
                          fontWeight: 600,
                          color: '#002329',
                          marginBottom: '8px'
                        }}>
                          {obj.type}: "{obj.name}"
                        </div>
                        <div style={{
                          fontSize: '13px',
                          color: '#ef4444',
                          marginTop: '8px'
                        }}>
                          Error: {obj.error}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>

            {/* Modal Footer */}
            <div style={{
              padding: '16px 24px',
              borderTop: '1px solid #e2e8f0',
              display: 'flex',
              justifyContent: 'flex-end',
              gap: '12px',
              flexShrink: 0
            }}>
              <button
                onClick={() => {
                  setShowPublishResultsModal(false);
                  if (publishResults.failed.length === 0) {
                    setTimeout(() => {
                      navigate('/dashboard');
                    }, 500);
                  }
                }}
                style={{
                  padding: '6px 12px',
                  backgroundColor: '#08979C',
                  color: '#ffffff',
                  border: 'none',
                  borderRadius: '6px',
                  fontSize: '14px',
                  fontWeight: 600,
                  cursor: 'pointer',
                  fontFamily: 'Poppins',
                  transition: 'background-color 0.2s'
                }}
                onMouseEnter={(e) => e.target.style.backgroundColor = '#067a7f'}
                onMouseLeave={(e) => e.target.style.backgroundColor = '#08979C'}
              >
                {publishResults.failed.length === 0 ? 'Continue' : 'Close'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

