import React, { useState, useEffect } from 'react';
import { Layout, Plus, BarChart3, TrendingUp, Users, FileText } from 'lucide-react';
import WidgetContainer from './WidgetContainer';
import WidgetConfigModal from './WidgetConfigModal';

const DashboardWidgetsManager = ({ availableWidgets = [] }) => {
  const [widgets, setWidgets] = useState([]);
  const [showAddMenu, setShowAddMenu] = useState(false);
  const [configuringWidget, setConfiguringWidget] = useState(null);

  useEffect(() => {
    loadWidgets();
  }, []);

  const loadWidgets = () => {
    try {
      const saved = localStorage.getItem('dashboard_widgets');
      if (saved) {
        setWidgets(JSON.parse(saved));
      } else {
        // Default widgets
        setWidgets([
          {
            id: 'widget_1',
            type: 'summary',
            title: 'Summary',
            component: <div>Summary widget content</div>
          }
        ]);
      }
    } catch (error) {
      console.error('Error loading widgets:', error);
    }
  };

  const saveWidgets = (newWidgets) => {
    try {
      localStorage.setItem('dashboard_widgets', JSON.stringify(newWidgets));
      setWidgets(newWidgets);
    } catch (error) {
      console.error('Error saving widgets:', error);
    }
  };

  const addWidget = (widgetType) => {
    const newWidget = {
      id: `widget_${Date.now()}`,
      type: widgetType,
      title: getWidgetTitle(widgetType),
      dataSource: 'dashboard',
      chartType: 'bar',
      color: '#08979C',
      showLegend: true,
      showGrid: true
    };
    // Generate component based on widget config
    newWidget.component = getWidgetComponent(newWidget);
    const newWidgets = [...widgets, newWidget];
    saveWidgets(newWidgets);
    setShowAddMenu(false);
    // Open configuration modal for new widget
    setConfiguringWidget(newWidget);
  };

  const handleConfigure = (widget) => {
    setConfiguringWidget(widget);
  };

  const handleSaveConfig = (widgetId, config) => {
    const updatedWidgets = widgets.map(w => {
      if (w.id === widgetId) {
        const updatedWidget = {
          ...w,
          ...config,
          component: getWidgetComponent({ ...w, ...config })
        };
        return updatedWidget;
      }
      return w;
    });
    saveWidgets(updatedWidgets);
  };

  const removeWidget = (widgetId) => {
    const newWidgets = widgets.filter(w => w.id !== widgetId);
    saveWidgets(newWidgets);
  };

  const getWidgetTitle = (type) => {
    const titles = {
      summary: 'Summary',
      chart: 'Chart',
      table: 'Table',
      metric: 'Metric',
      list: 'List'
    };
    return titles[type] || 'Widget';
  };

  const getWidgetComponent = (widget) => {
    const type = widget.type || 'summary';
    const color = widget.color || '#08979C';

    switch (type) {
      case 'summary':
        return (
          <div style={{ padding: '12px', background: '#f9fafb', borderRadius: '6px' }}>
            <div style={{ fontSize: '24px', fontWeight: '600', color: color }}>0</div>
            <div style={{ fontSize: '12px', color: '#666' }}>Total Items</div>
          </div>
        );
      case 'chart':
        return (
          <div style={{ padding: '12px', textAlign: 'center', color: '#666' }}>
            <BarChart3 size={32} style={{ opacity: 0.3, color: color }} />
            <div style={{ fontSize: '12px', marginTop: '8px' }}>
              {widget.chartType ? `${widget.chartType.charAt(0).toUpperCase() + widget.chartType.slice(1)} Chart` : 'Chart widget'}
            </div>
          </div>
        );
      case 'table':
        return (
          <div style={{ padding: '12px', textAlign: 'center', color: '#666' }}>
            <FileText size={32} style={{ opacity: 0.3, color: color }} />
            <div style={{ fontSize: '12px', marginTop: '8px' }}>Table widget</div>
          </div>
        );
      case 'metric':
        return (
          <div style={{ padding: '12px', background: '#f9fafb', borderRadius: '6px' }}>
            <div style={{ fontSize: '20px', fontWeight: '600', color: color }}>
              {widget.metricValue || '0%'}
            </div>
            <div style={{ fontSize: '12px', color: '#666' }}>
              {widget.metricLabel || 'Metric'}
            </div>
          </div>
        );
      default:
        return <div>Widget content</div>;
    }
  };

  const widgetTypes = [
    { type: 'summary', label: 'Summary', icon: BarChart3 },
    { type: 'chart', label: 'Chart', icon: TrendingUp },
    { type: 'table', label: 'Table', icon: FileText },
    { type: 'metric', label: 'Metric', icon: Users }
  ];

  return (
    <div style={{ padding: '24px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '24px' }}>
        <h2 style={{ fontSize: '20px', fontWeight: '600', margin: 0, display: 'flex', alignItems: 'center', gap: '8px' }}>
          <Layout size={24} color="#0176d3" />
          Dashboard Widgets
        </h2>
        <div style={{ position: 'relative' }}>
          <button
            onClick={() => setShowAddMenu(!showAddMenu)}
            style={{
              padding: '8px 16px',
              background: '#0176d3',
              color: '#fff',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer',
              fontSize: '13px',
              display: 'flex',
              alignItems: 'center',
              gap: '6px'
            }}
          >
            <Plus size={16} />
            Add Widget
          </button>

          {showAddMenu && (
            <div
              style={{
                position: 'absolute',
                top: '100%',
                right: 0,
                marginTop: '8px',
                background: '#fff',
                border: '1px solid #e5e7eb',
                borderRadius: '6px',
                boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
                padding: '8px',
                minWidth: '200px',
                zIndex: 1000
              }}
            >
              {widgetTypes.map(wt => {
                const Icon = wt.icon;
                return (
                  <div
                    key={wt.type}
                    onClick={() => addWidget(wt.type)}
                    style={{
                      padding: '10px 12px',
                      cursor: 'pointer',
                      borderRadius: '4px',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '10px',
                      fontSize: '13px',
                      transition: 'background 0.2s'
                    }}
                    onMouseEnter={(e) => e.target.style.background = '#f9fafb'}
                    onMouseLeave={(e) => e.target.style.background = 'transparent'}
                  >
                    <Icon size={16} color="#0176d3" />
                    {wt.label}
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: '16px' }}>
        {widgets.map(widget => (
          <WidgetContainer
            key={widget.id}
            widget={widget}
            onRemove={removeWidget}
            onConfigure={(w) => console.log('Configure:', w)}
          />
        ))}
      </div>

      {widgets.length === 0 && (
        <div style={{ textAlign: 'center', padding: '40px', color: '#666' }}>
          <Layout size={32} style={{ marginBottom: '12px', opacity: 0.5 }} />
          <p>No widgets. Add widgets to customize your dashboard.</p>
        </div>
      )}
    </div>
  );
};

export default DashboardWidgetsManager;
