import React, { useState } from 'react';
import { X, Plus, Trash2 } from 'lucide-react';

const OPERATORS = [
  { value: 'equals', label: 'Equals (=)' },
  { value: 'notEquals', label: 'Not Equals (≠)' },
  { value: 'contains', label: 'Contains' },
  { value: 'notContains', label: 'Does Not Contain' },
  { value: 'startsWith', label: 'Starts With' },
  { value: 'endsWith', label: 'Ends With' },
  { value: 'greaterThan', label: 'Greater Than (>)' },
  { value: 'lessThan', label: 'Less Than (<)' },
  { value: 'greaterThanOrEqual', label: 'Greater Than or Equal (≥)' },
  { value: 'lessThanOrEqual', label: 'Less Than or Equal (≤)' },
  { value: 'isEmpty', label: 'Is Empty' },
  { value: 'isNotEmpty', label: 'Is Not Empty' }
];

const CaseFilterBuilder = ({ availableFields, filters, onSubmit, onClear, onClose }) => {
  const [localFilters, setLocalFilters] = useState(filters.length > 0 ? filters : [{ field: '', operator: 'equals', value: '' }]);

  const handleAddFilter = () => {
    setLocalFilters([...localFilters, { field: '', operator: 'equals', value: '' }]);
  };

  const handleRemoveFilter = (index) => {
    if (localFilters.length > 1) {
      setLocalFilters(localFilters.filter((_, i) => i !== index));
    }
  };

  const handleFilterChange = (index, key, value) => {
    const updated = [...localFilters];
    updated[index] = { ...updated[index], [key]: value };
    // Clear value if operator is isEmpty or isNotEmpty
    if (key === 'operator' && (value === 'isEmpty' || value === 'isNotEmpty')) {
      updated[index].value = '';
    }
    setLocalFilters(updated);
  };

  const handleSubmit = () => {
    // Validate filters
    const validFilters = localFilters.filter(f => f.field && f.operator);
    onSubmit(validFilters);
  };

  const getFieldType = (fieldName) => {
    const field = availableFields.find(f => f.name === fieldName);
    return field ? field.type : 'string';
  };

  const renderValueInput = (filter, index) => {
    const fieldType = getFieldType(filter.field);
    const operator = filter.operator;

    // No value input for isEmpty/isNotEmpty
    if (operator === 'isEmpty' || operator === 'isNotEmpty') {
      return null;
    }

    // Boolean field
    if (fieldType === 'boolean' || fieldType === 'checkbox') {
      return (
        <select
          value={filter.value || ''}
          onChange={(e) => handleFilterChange(index, 'value', e.target.value)}
          className="filter-value-input"
        >
          <option value="">--Select--</option>
          <option value="true">True</option>
          <option value="false">False</option>
        </select>
      );
    }

    // Date field
    if (fieldType === 'date' || fieldType === 'datetime') {
      return (
        <input
          type={fieldType === 'date' ? 'date' : 'datetime-local'}
          value={filter.value || ''}
          onChange={(e) => handleFilterChange(index, 'value', e.target.value)}
          className="filter-value-input"
        />
      );
    }

    // Number field
    if (fieldType === 'int' || fieldType === 'double' || fieldType === 'currency' || fieldType === 'percent') {
      return (
        <input
          type="number"
          value={filter.value || ''}
          onChange={(e) => handleFilterChange(index, 'value', e.target.value)}
          className="filter-value-input"
        />
      );
    }

    // Text field (default)
    return (
      <input
        type="text"
        value={filter.value || ''}
        onChange={(e) => handleFilterChange(index, 'value', e.target.value)}
        placeholder="Enter value"
        className="filter-value-input"
      />
    );
  };

  return (
    <div className="case-filter-builder">
      <div className="filter-builder-header">
        <h3>Filter Cases</h3>
        <button onClick={onClose} className="btn-close-filter">
          <X size={18} />
        </button>
      </div>

      <div className="filter-builder-content">
        {localFilters.map((filter, index) => (
          <div key={index} className="filter-row">
            <select
              value={filter.field}
              onChange={(e) => handleFilterChange(index, 'field', e.target.value)}
              className="filter-field-select"
            >
              <option value="">--Select Field--</option>
              {availableFields.map(field => (
                <option key={field.name} value={field.name}>
                  {field.label}
                </option>
              ))}
            </select>

            <select
              value={filter.operator}
              onChange={(e) => handleFilterChange(index, 'operator', e.target.value)}
              className="filter-operator-select"
              disabled={!filter.field}
            >
              {OPERATORS.map(op => (
                <option key={op.value} value={op.value}>
                  {op.label}
                </option>
              ))}
            </select>

            {renderValueInput(filter, index)}

            {localFilters.length > 1 && (
              <button
                onClick={() => handleRemoveFilter(index)}
                className="btn-remove-filter"
                title="Remove filter"
              >
                <Trash2 size={16} />
              </button>
            )}
          </div>
        ))}

        <button onClick={handleAddFilter} className="btn-add-filter">
          <Plus size={16} />
          Add Filter
        </button>
      </div>

      <div className="filter-builder-actions">
        <button onClick={onClear} className="btn-clear-filters">
          Clear All
        </button>
        <button onClick={handleSubmit} className="btn-submit-filters">
          Apply Filters
        </button>
      </div>
    </div>
  );
};

export default CaseFilterBuilder;
