import React, { useState, useEffect } from 'react';
import { ArrowLeft, Edit, Save, X, Loader } from 'lucide-react';

const CaseDetailView = ({ caseData, availableFields, onUpdate, onBack }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editedData, setEditedData] = useState(caseData);
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    setEditedData(caseData);
  }, [caseData]);

  const handleFieldChange = (fieldName, value) => {
    setEditedData(prev => ({
      ...prev,
      [fieldName]: value
    }));
  };

  const handleSave = async () => {
    setSaving(true);
    await onUpdate(caseData.Id, editedData);
    setSaving(false);
    setIsEditing(false);
  };

  // Helper function to get field value, handling relationship fields
  const getFieldValue = (field) => {
    // Handle relationship fields - show name instead of ID
    if (field.type === 'reference') {
      // Check for flattened relationship fields (e.g., Contact.Name, Owner.Name)
      if (field.name === 'ContactId') {
        return editedData['Contact.Name'] || editedData[field.name] || '';
      } else if (field.name === 'OwnerId') {
        return editedData['Owner.Name'] || editedData[field.name] || '';
      } else if (field.relationshipName) {
        // For custom relationships, try to get the Name field
        const relName = field.relationshipName.endsWith('__r') 
          ? field.relationshipName 
          : `${field.relationshipName}__r`;
        const nameField = `${relName}.Name`;
        return editedData[nameField] || editedData[field.name] || '';
      }
    }

    // Handle special fields like Contact.Email
    if (field.name === 'Contact.Email') {
      return editedData['Contact.Email'] || editedData.Contact?.Email || '';
    }

    const value = editedData[field.name];

    if (value === null || value === undefined || value === '') {
      return '';
    }

    // Format dates
    if (field.type === 'date' || field.type === 'datetime') {
      if (value instanceof Date) {
        return value.toLocaleString();
      }
      if (typeof value === 'string') {
        const date = new Date(value);
        if (!isNaN(date.getTime())) {
          return date.toLocaleString();
        }
      }
    }

    return String(value);
  };

  const renderFieldInput = (field) => {
    const displayValue = getFieldValue(field);

    if (!isEditing) {
      return <span className="detail-field-text">{displayValue || ''}</span>;
    }

    switch (field.type) {
      case 'picklist':
        return (
          <select
            value={editedData[field.name] || ''}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            className="detail-field-input"
          >
            <option value="">--None--</option>
            {field.picklistValues?.map(pv => (
              <option key={pv} value={pv}>{pv}</option>
            ))}
          </select>
        );
      case 'boolean':
        return (
          <input
            type="checkbox"
            checked={!!editedData[field.name]}
            onChange={(e) => handleFieldChange(field.name, e.target.checked)}
            className="detail-field-checkbox"
          />
        );
      case 'textarea':
        return (
          <textarea
            value={editedData[field.name] || ''}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            className="detail-field-textarea"
          />
        );
      case 'date':
        return (
          <input
            type="date"
            value={editedData[field.name] ? new Date(editedData[field.name]).toISOString().split('T')[0] : ''}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            className="detail-field-input"
          />
        );
      case 'datetime':
        return (
          <input
            type="datetime-local"
            value={editedData[field.name] ? new Date(editedData[field.name]).toISOString().slice(0, 16) : ''}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            className="detail-field-input"
          />
        );
      case 'int':
      case 'double':
      case 'currency':
      case 'percent':
        return (
          <input
            type="number"
            value={editedData[field.name] || ''}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            className="detail-field-input"
          />
        );
      case 'reference':
        // For reference fields, show the name in view mode, but allow editing the ID
        if (!isEditing) {
          return <span className="detail-field-text">{value || ''}</span>;
        }
        return (
          <input
            type="text"
            value={editedData[field.name] || ''}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            placeholder={`Enter ${field.label} ID`}
            className="detail-field-input"
          />
        );
      default:
        return (
          <input
            type="text"
            value={editedData[field.name] || ''}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            className="detail-field-input"
          />
        );
    }
  };

  // Define field sections matching the screenshot layout
  const getFieldSections = () => {
    // Field mapping based on screenshot
    const fieldMap = new Map();
    availableFields.forEach(field => {
      fieldMap.set(field.name, field);
    });

    // Case Details Section - in exact order from screenshot
    const caseDetailsFields = [
      'OwnerId',           // Case Owner
      'Group',             // Group
      'Origin',            // Case Origin
      'Priority',          // Priority
      'Contributor_Project__c', // Contributor Project
      'Project__c',        // Project
      'Project_Support_Lead__c', // Project Support Lead
      'Contributor_Status__c',   // Contributor Status
      'Target_SLA__c',     // Target SLA
      'External_SLA__c',   // External SLA
      'SLA_Status__c',     // SLA Status
      'Project_Team__c',   // Project Team
      'CaseNumber',        // Case Number
      'Total_Case_Duration__c', // Total Case Duration
      'Active_Case_Duration__c', // Active Case Duration
      'Status',            // Status
      'Case_Action_Taken__c', // Case Action Taken
      'AutoCase_Flow__c',  // AutoCase Flow
      'AutoCase_Date_Time__c', // AutoCase Date/Time
      'CreatedDate',       // Date/Time Opened
      'Related_Case__c'    // Related Case
    ].map(fieldName => fieldMap.get(fieldName)).filter(Boolean);

    // Contributor Information Section
    const contributorFields = [
      'ContactId',         // Contributor Name
      'Contact.Email',     // Contributor Email (we'll handle this specially)
      'AC_ID__c',          // AC ID
      'AC_Account_Created_Date__c', // AC Account Created Date
      'Contributor_Status__c',      // Contributor Status (if different from above)
      'Primary_Language_Spoken__c', // Primary Language Spoken
      'Currently_Residing_Country__c', // Currently Residing Country
      'Currently_Residing_US_State__c' // Currently Residing US State
    ].map(fieldName => {
      if (fieldName === 'Contact.Email') {
        // Create a virtual field for Contact.Email
        return {
          name: 'Contact.Email',
          label: 'Contributor Email',
          type: 'email'
        };
      }
      return fieldMap.get(fieldName);
    }).filter(Boolean);

    // Description Information Section
    const descriptionFields = [
      'Subject',           // Subject
      'Type'               // Case Type
    ].map(fieldName => fieldMap.get(fieldName)).filter(Boolean);

    return {
      caseDetails: caseDetailsFields,
      contributor: contributorFields,
      description: descriptionFields
    };
  };

  const sections = getFieldSections();

  return (
    <div className="case-detail-view">
      <div className="case-detail-header">
        <button onClick={onBack} className="btn-back">
          <ArrowLeft size={18} />
          <span>Back to List</span>
        </button>
        <div className="case-detail-title">
          <h2>Case: {caseData.Subject || caseData.CaseNumber || caseData.Id}</h2>
        </div>
        <div className="case-detail-actions">
          {!isEditing ? (
            <button onClick={() => setIsEditing(true)} className="btn-edit">
              <Edit size={16} />
              <span>Edit</span>
            </button>
          ) : (
            <>
              <button onClick={handleSave} disabled={saving} className="btn-save">
                {saving ? <Loader size={16} className="spinner" /> : <Save size={16} />}
                <span>Save</span>
              </button>
              <button onClick={() => setIsEditing(false)} className="btn-cancel">
                <X size={16} />
                <span>Cancel</span>
              </button>
            </>
          )}
        </div>
      </div>

      <div className="case-detail-content">
        {/* Case Details Section */}
        <div className="case-detail-section">
          <div className="case-detail-section-header">
            <h3>Case Details</h3>
          </div>
          <div className="case-detail-section-content">
            <div className="case-detail-grid">
              {sections.caseDetails.map((field, index) => (
                <div key={field.name || index} className="detail-field-group">
                  <label className="detail-field-label">{field.label}:</label>
                  <div className="detail-field-value">
                    {renderFieldInput(field)}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Contributor Information Section */}
        <div className="case-detail-section">
          <div className="case-detail-section-header">
            <h3>Contributor Information</h3>
          </div>
          <div className="case-detail-section-content">
            <div className="case-detail-grid">
              {sections.contributor.map((field, index) => {
                // Handle Contact.Email specially
                if (field.name === 'Contact.Email') {
                  const emailValue = editedData['Contact.Email'] || editedData.Contact?.Email || '';
                  return (
                    <div key="Contact.Email" className="detail-field-group">
                      <label className="detail-field-label">Contributor Email:</label>
                      <div className="detail-field-value">
                        {!isEditing ? (
                          <span className="detail-field-text">{emailValue || ''}</span>
                        ) : (
                          <input
                            type="email"
                            value={editedData['Contact.Email'] || ''}
                            onChange={(e) => handleFieldChange('Contact.Email', e.target.value)}
                            className="detail-field-input"
                          />
                        )}
                      </div>
                    </div>
                  );
                }
                return (
                  <div key={field.name || index} className="detail-field-group">
                    <label className="detail-field-label">{field.label}:</label>
                    <div className="detail-field-value">
                      {renderFieldInput(field)}
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>

        {/* Description Information Section */}
        <div className="case-detail-section">
          <div className="case-detail-section-header">
            <h3>Description Information</h3>
          </div>
          <div className="case-detail-section-content">
            <div className="case-detail-grid">
              {sections.description.map((field, index) => (
                <div key={field.name || index} className="detail-field-group">
                  <label className="detail-field-label">{field.label}:</label>
                  <div className="detail-field-value">
                    {renderFieldInput(field)}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CaseDetailView;
