                )}
                  <input type="hidden" {...register('reviewerId')} />
                  <input type="hidden" {...register('reviewerName')} />
                </div>
              </div>

              {/* Comments */}
              <div className="form-group" style={{ gridColumn: 'span 4' }}>
                <label className="form-label" style={{ display: 'flex', alignItems: 'center', gap: '4px', marginBottom: '8px', fontSize: '13px', fontWeight: '500', color: '#002329' }}>Comments</label>
                <textarea
                  {...register('comments')}
                  rows={4}
                  placeholder="Enter comments..."
                  style={{
                    width: '100%',
                    padding: '10px 12px',
                    border: '1px solid #d8dde6',
                    borderRadius: '6px',
                    fontSize: '14px',
                    fontFamily: 'inherit',
                    resize: 'vertical'
                  }}
                />
              </div>
            </div>

            {/* Action Buttons */}
            <div className="form-actions" style={{ marginTop: '24px', paddingTop: '16px', borderTop: '1px solid #d8dde6', padding: '24px', display: 'flex', gap: '12px', alignItems: 'center', width: '100%', justifyContent: 'flex-end' }}>
                <button 
                  type="button" 
                  className="btn-secondary" 
                  onClick={handlePreview}
                  style={{ display: 'flex', alignItems: 'center', gap: '6px' }}
                >
                  <Eye size={16} />
                  Preview
                </button>
                <button
                  type="submit"
                  className="btn-primary"
                  disabled={publishing}
                  style={{ display: 'flex', alignItems: 'center', gap: '6px' }}
                >
                  {publishing ? (
                    <>
                      <Loader size={16} className="spinning" style={{ color: '#08979C' }} />
                      Publishing...
                    </>
                  ) : (
                    <>
                      <Send size={16} />
                      Publish
                    </>
                  )}
                </button>
              </div>
          </form>
        </div>
      </div>

      {/* Preview Modal */}
      <PreviewModal
        show={showPreviewModal}
        onClose={() => setShowPreviewModal(false)}
        formData={formData}
        objectType="Contributor Review"
        displayObjectLabel="Contributor Review"
      />

      {/* Publish Confirmation Modal */}
      <ConfirmModal
        show={showPublishConfirm}
        message="Are you sure you want to publish this Contributor Review to Salesforce? This will create a new record."
        onConfirm={handlePublish}
        onCancel={() => setShowPublishConfirm(false)}
        confirmText="Publish"
        cancelText="Cancel"
        type="warning"
      />
    </div>
  );
};

export default AddContributorReview;
