import React, { useState, useEffect } from 'react';
import { ArrowLeft, Edit, Save, X, Loader } from 'lucide-react';

const CaseDetailView = ({ caseData, availableFields, onUpdate, onBack }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editedData, setEditedData] = useState(caseData);
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    setEditedData(caseData);
  }, [caseData]);

  const handleFieldChange = (fieldName, value) => {
    setEditedData(prev => ({
      ...prev,
      [fieldName]: value
    }));
  };

  const handleSave = async () => {
    setSaving(true);
    await onUpdate(caseData.Id, editedData);
    setSaving(false);
    setIsEditing(false);
  };

  const renderFieldInput = (field) => {
    const value = editedData[field.name];
    if (!isEditing) {
      return <span className="detail-field-text">{value !== null && value !== undefined ? String(value) : 'N/A'}</span>;
    }

    switch (field.type) {
      case 'picklist':
        return (
          <select
            value={value || ''}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            className="detail-field-input"
          >
            <option value="">--None--</option>
            {field.picklistValues?.map(pv => (
              <option key={pv} value={pv}>{pv}</option>
            ))}
          </select>
        );
      case 'boolean':
        return (
          <input
            type="checkbox"
            checked={!!value}
            onChange={(e) => handleFieldChange(field.name, e.target.checked)}
            className="detail-field-checkbox"
          />
        );
      case 'textarea':
        return (
          <textarea
            value={value || ''}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            className="detail-field-textarea"
          />
        );
      case 'date':
        return (
          <input
            type="date"
            value={value ? new Date(value).toISOString().split('T')[0] : ''}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            className="detail-field-input"
          />
        );
      case 'datetime':
        return (
          <input
            type="datetime-local"
            value={value ? new Date(value).toISOString().slice(0, 16) : ''}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            className="detail-field-input"
          />
        );
      case 'int':
      case 'double':
      case 'currency':
      case 'percent':
        return (
          <input
            type="number"
            value={value || ''}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            className="detail-field-input"
          />
        );
      case 'reference':
        return (
          <input
            type="text"
            value={value || ''}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            placeholder={`Enter ${field.label} ID`}
            className="detail-field-input"
          />
        );
      default:
        return (
          <input
            type="text"
            value={value || ''}
            onChange={(e) => handleFieldChange(field.name, e.target.value)}
            className="detail-field-input"
          />
        );
    }
  };

  const fieldPairs = [];
  for (let i = 0; i < availableFields.length; i += 2) {
    fieldPairs.push([
      availableFields[i],
      availableFields[i + 1]
    ]);
  }

  return (
    <div className="case-detail-view">
      <div className="case-detail-header">
        <button onClick={onBack} className="btn-back">
          <ArrowLeft size={18} />
          <span>Back to List</span>
        </button>
        <div className="case-detail-title">
          <h2>Case: {caseData.CaseNumber || caseData.Id}</h2>
        </div>
        <div className="case-detail-actions">
          {!isEditing ? (
            <button onClick={() => setIsEditing(true)} className="btn-edit">
              <Edit size={16} />
              <span>Edit</span>
            </button>
          ) : (
            <>
              <button onClick={handleSave} disabled={saving} className="btn-save">
                {saving ? <Loader size={16} className="spinner" /> : <Save size={16} />}
                <span>Save</span>
              </button>
              <button onClick={() => setIsEditing(false)} className="btn-cancel">
                <X size={16} />
                <span>Cancel</span>
              </button>
            </>
          )}
        </div>
      </div>

      <div className="case-detail-content">
        <div className="case-detail-grid">
          {fieldPairs.map((pair, index) => (
            <React.Fragment key={index}>
              {pair[0] && (
                <div className="detail-field-group">
                  <label className="detail-field-label">{pair[0].label}:</label>
                  <div className="detail-field-value">
                    {renderFieldInput(pair[0])}
                  </div>
                </div>
              )}
              {pair[1] && (
                <div className="detail-field-group">
                  <label className="detail-field-label">{pair[1].label}:</label>
                  <div className="detail-field-value">
                    {renderFieldInput(pair[1])}
                  </div>
                </div>
              )}
            </React.Fragment>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CaseDetailView;
