import React, { useState, useEffect } from 'react';
import { X, Filter } from 'lucide-react';
import '../CaseManagement/CaseFilterBuilder.css';

const WorkStreamFilter = ({ showFilters, onToggleFilters, onApplyFilters, onClearFilters, filters }) => {
  const [projectStatusValues, setProjectStatusValues] = useState([]);
  const [projectObjectiveStatusValues, setProjectObjectiveStatusValues] = useState([]);
  const [selectedField, setSelectedField] = useState('');
  const [selectedValues, setSelectedValues] = useState([]);
  const [availableValues, setAvailableValues] = useState([]);
  const [loadingValues, setLoadingValues] = useState(false);

  // Default status values for Project Objective Status
  const defaultProjectObjectiveStatuses = ['Open', 'Paused', 'Hidden'];

  // Common project status values (these should ideally come from API)
  const commonProjectStatuses = ['Open', 'Closed', 'Paused', 'Cancelled', 'On Hold'];

  useEffect(() => {
    // Initialize with default filters
    if (filters.projectObjectiveStatus && filters.projectObjectiveStatus.length > 0) {
      setProjectObjectiveStatusValues(filters.projectObjectiveStatus);
    } else {
      // Default: Open, Paused, Hidden
      setProjectObjectiveStatusValues(defaultProjectObjectiveStatuses);
    }

    if (filters.projectStatus && filters.projectStatus.length > 0) {
      setProjectStatusValues(filters.projectStatus);
    }
  }, [filters]);

  const handleFieldChange = (field) => {
    setSelectedField(field);
    setSelectedValues([]);

    if (field === 'projectStatus') {
      setAvailableValues(commonProjectStatuses);
    } else if (field === 'projectObjectiveStatus') {
      setAvailableValues(defaultProjectObjectiveStatuses);
    } else {
      setAvailableValues([]);
    }
  };

  const handleValueToggle = (value) => {
    setSelectedValues(prev => {
      if (prev.includes(value)) {
        return prev.filter(v => v !== value);
      } else {
        return [...prev, value];
      }
    });
  };

  const handleAddFilter = () => {
    if (!selectedField || selectedValues.length === 0) {
      return;
    }

    const newFilters = { ...filters };
    if (selectedField === 'projectStatus') {
      newFilters.projectStatus = [...selectedValues];
    } else if (selectedField === 'projectObjectiveStatus') {
      newFilters.projectObjectiveStatus = [...selectedValues];
    }

    onApplyFilters(newFilters);
    setSelectedField('');
    setSelectedValues([]);
    setAvailableValues([]);
  };

  const handleClearAll = () => {
    setProjectStatusValues([]);
    setProjectObjectiveStatusValues([]);
    onClearFilters();
  };

  const handleRemoveFilter = (field) => {
    const newFilters = { ...filters };
    if (field === 'projectStatus') {
      delete newFilters.projectStatus;
    } else if (field === 'projectObjectiveStatus') {
      delete newFilters.projectObjectiveStatus;
    }
    onApplyFilters(newFilters);
  };

  return (
    <>
      <button
        onClick={onToggleFilters}
        className={`btn-action ${showFilters ? 'active' : ''}`}
        title="Filter workstreams"
        style={{
          display: 'flex',
          alignItems: 'center',
          gap: '6px',
          padding: '8px 16px',
          background: showFilters ? '#0176d3' : 'transparent',
          color: showFilters ? '#fff' : '#0176d3',
          border: '1px solid #0176d3',
          borderRadius: '4px',
          cursor: 'pointer',
          fontSize: '14px',
          fontWeight: '500'
        }}
      >
        <Filter size={16} />
        <span>Filter</span>
        {(projectStatusValues.length > 0 || projectObjectiveStatusValues.length > 0) && (
          <span style={{
            background: showFilters ? '#fff' : '#0176d3',
            color: showFilters ? '#0176d3' : '#fff',
            borderRadius: '10px',
            padding: '2px 6px',
            fontSize: '11px',
            fontWeight: '600',
            marginLeft: '4px'
          }}>
            {projectStatusValues.length + projectObjectiveStatusValues.length}
          </span>
        )}
      </button>

      {showFilters && (
        <div className="case-filter-builder" style={{ marginTop: '16px', marginBottom: '16px' }}>
          <div className="filter-builder-header">
            <h3>Filter Workstreams</h3>
            <button className="btn-close-filter" onClick={onToggleFilters}>
              <X size={18} />
            </button>
          </div>

          <div className="filter-builder-content">
            {/* Active Filters Display */}
            {(projectStatusValues.length > 0 || projectObjectiveStatusValues.length > 0) && (
              <div style={{ marginBottom: '16px', padding: '12px', background: '#f8fafc', borderRadius: '4px' }}>
                <div style={{ fontSize: '12px', fontWeight: '600', marginBottom: '8px', color: '#374151' }}>
                  Active Filters:
                </div>
                {projectStatusValues.length > 0 && (
                  <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '4px' }}>
                    <span style={{ fontSize: '12px', color: '#6b7280' }}>Project Status:</span>
                    <div style={{ display: 'flex', flexWrap: 'wrap', gap: '4px' }}>
                      {projectStatusValues.map(value => (
                        <span
                          key={value}
                          style={{
                            background: '#dbeafe',
                            color: '#1e40af',
                            padding: '2px 8px',
                            borderRadius: '12px',
                            fontSize: '11px',
                            display: 'flex',
                            alignItems: 'center',
                            gap: '4px'
                          }}
                        >
                          {value}
                          <button
                            onClick={() => handleRemoveFilter('projectStatus')}
                            style={{
                              background: 'none',
                              border: 'none',
                              color: '#1e40af',
                              cursor: 'pointer',
                              padding: '0',
                              marginLeft: '4px',
                              display: 'flex',
                              alignItems: 'center'
                            }}
                          >
                            <X size={12} />
                          </button>
                        </span>
                      ))}
                    </div>
                  </div>
                )}
                {projectObjectiveStatusValues.length > 0 && (
                  <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                    <span style={{ fontSize: '12px', color: '#6b7280' }}>Project Objective Status:</span>
                    <div style={{ display: 'flex', flexWrap: 'wrap', gap: '4px' }}>
                      {projectObjectiveStatusValues.map(value => (
                        <span
                          key={value}
                          style={{
                            background: '#dbeafe',
                            color: '#1e40af',
                            padding: '2px 8px',
                            borderRadius: '12px',
                            fontSize: '11px',
                            display: 'flex',
                            alignItems: 'center',
                            gap: '4px'
                          }}
                        >
                          {value}
                          <button
                            onClick={() => handleRemoveFilter('projectObjectiveStatus')}
                            style={{
                              background: 'none',
                              border: 'none',
                              color: '#1e40af',
                              cursor: 'pointer',
                              padding: '0',
                              marginLeft: '4px',
                              display: 'flex',
                              alignItems: 'center'
                            }}
                          >
                            <X size={12} />
                          </button>
                        </span>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            )}

            {/* Add New Filter */}
            <div style={{ border: '1px solid #e5e7eb', borderRadius: '4px', padding: '12px', background: '#fff' }}>
              <div style={{ marginBottom: '12px' }}>
                <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '4px', color: '#374151' }}>
                  Select Field
                </label>
                <select
                  value={selectedField}
                  onChange={(e) => handleFieldChange(e.target.value)}
                  style={{
                    width: '100%',
                    padding: '6px 10px',
                    border: '1px solid #d1d5db',
                    borderRadius: '4px',
                    fontSize: '13px'
                  }}
                >
                  <option value="">Select Field</option>
                  <option value="projectStatus">Project Status</option>
                  <option value="projectObjectiveStatus">Project Objective Status</option>
                </select>
              </div>

              {selectedField && (
                <div style={{ marginBottom: '12px' }}>
                  <label style={{ display: 'block', fontSize: '12px', fontWeight: '500', marginBottom: '4px', color: '#374151' }}>
                    Select Values (Multi-select)
                  </label>
                  <div style={{
                    border: '1px solid #d1d5db',
                    borderRadius: '4px',
                    padding: '8px',
                    maxHeight: '150px',
                    overflowY: 'auto',
                    background: '#fff'
                  }}>
                    {availableValues.length === 0 ? (
                      <div style={{ padding: '8px', color: '#6b7280', fontSize: '12px' }}>No values available</div>
                    ) : (
                      availableValues.map(value => (
                        <label
                          key={value}
                          style={{
                            display: 'flex',
                            alignItems: 'center',
                            gap: '8px',
                            padding: '4px 0',
                            cursor: 'pointer',
                            fontSize: '13px'
                          }}
                        >
                          <input
                            type="checkbox"
                            checked={selectedValues.includes(value)}
                            onChange={() => handleValueToggle(value)}
                          />
                          <span>{value}</span>
                        </label>
                      ))
                    )}
                  </div>
                </div>
              )}

              <button
                onClick={handleAddFilter}
                disabled={!selectedField || selectedValues.length === 0}
                style={{
                  width: '100%',
                  padding: '8px 16px',
                  background: (!selectedField || selectedValues.length === 0) ? '#e5e7eb' : '#0176d3',
                  color: (!selectedField || selectedValues.length === 0) ? '#9ca3af' : '#fff',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: (!selectedField || selectedValues.length === 0) ? 'not-allowed' : 'pointer',
                  fontSize: '13px',
                  fontWeight: '500'
                }}
              >
                Add Filter
              </button>
            </div>
          </div>

          <div className="filter-builder-actions">
            <button className="btn-clear-filters" onClick={handleClearAll}>
              Clear All
            </button>
            <button className="btn-submit-filters" onClick={() => onApplyFilters(filters)}>
              Apply Filters
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default WorkStreamFilter;
