      if (!window.confirm(warningMessage)) {
        return;
      }
      toast.warning(`Publishing with ${fieldsWithoutValues.length} field(s) without values: ${fieldsWithoutValues.join(', ')}`);
    }

    setSubmitting(true);
    try {
      const allFormData = watch();
