import React from 'react';
import './RecordsCount.css';

const RecordsCount = ({ count, loading }) => {
  if (loading) {
    return (
      <div className="records-count">
        <div className="records-count-loading">Loading...</div>
      </div>
    );
  }

  const formatNumber = (num) => {
    if (num === null || num === undefined) return '0';
    return new Intl.NumberFormat('en-US').format(num);
  };

  return (
    <div className="records-count">
      <div className="records-count-label">No. of records</div>
      <div className="records-count-value">{formatNumber(count)}</div>
    </div>
  );
};

export default RecordsCount;
