import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../../context/AuthContext';
import Sidebar from '../../components/Sidebar';
import { Menu, LogOut, RefreshCw, Loader } from 'lucide-react';
import toast from 'react-hot-toast';
import apiClient from '../../config/api';
import OverviewCards from './components/OverviewCards';
import FunnelChart from './components/FunnelChart';
import FinancialChart from './components/FinancialChart';
import ObjectiveChart from './components/ObjectiveChart';
import TeamChart from './components/TeamChart';
import QueueChart from './components/QueueChart';
import '../../styles/ProjectPerformance.css';
import '../../styles/Sidebar.css';
import '../../styles/GlobalHeader.css';

const ProjectPerformance = () => {
  const { user, logout } = useAuth();
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [activeTab, setActiveTab] = useState('overview');
  const [selectedAccount, setSelectedAccount] = useState('all');
  const [accounts, setAccounts] = useState([]);

  // Data states
  const [overviewData, setOverviewData] = useState(null);
  const [funnelData, setFunnelData] = useState(null);
  const [financialData, setFinancialData] = useState(null);
  const [objectivesData, setObjectivesData] = useState(null);
  const [teamData, setTeamData] = useState(null);
  const [queueData, setQueueData] = useState(null);

  // Error states
  const [errors, setErrors] = useState({});

  // Fetch accounts
  const fetchAccounts = useCallback(async () => {
    try {
      const response = await apiClient.get('/project-performance/accounts');
      if (response.data.success) {
        setAccounts(response.data.data || []);
      }
    } catch (error) {
      console.error('Error fetching accounts:', error);
    }
  }, []);

  // Fetch overview data
  const fetchOverview = useCallback(async () => {
    try {
      const params = selectedAccount !== 'all' ? { account: selectedAccount } : {};
      const response = await apiClient.get('/project-performance/overview', {
        params,
        timeout: 300000
      });
      if (response.data.success) {
        setOverviewData(response.data.data);
        setErrors(prev => ({ ...prev, overview: null }));
      }
    } catch (error) {
      console.error('Error fetching overview:', error);
      setErrors(prev => ({ ...prev, overview: error.message || 'Failed to load overview data' }));
      toast.error('Failed to load overview data');
    }
  }, [selectedAccount]);

  // Fetch funnel data
  const fetchFunnel = useCallback(async () => {
    try {
      const params = selectedAccount !== 'all' ? { account: selectedAccount } : {};
      const response = await apiClient.get('/project-performance/funnel', {
        params,
        timeout: 300000
      });
      if (response.data.success) {
        setFunnelData(response.data.data);
        setErrors(prev => ({ ...prev, funnel: null }));
      }
    } catch (error) {
      console.error('Error fetching funnel:', error);
      setErrors(prev => ({ ...prev, funnel: error.message || 'Failed to load funnel data' }));
      toast.error('Failed to load funnel data');
    }
  }, [selectedAccount]);

  // Fetch financial data
  const fetchFinancial = useCallback(async () => {
    try {
      const params = selectedAccount !== 'all' ? { account: selectedAccount } : {};
      const response = await apiClient.get('/project-performance/financial', {
        params,
        timeout: 300000
      });
      if (response.data.success) {
        setFinancialData(response.data.data);
        setErrors(prev => ({ ...prev, financial: null }));
      }
    } catch (error) {
      console.error('Error fetching financial:', error);
      setErrors(prev => ({ ...prev, financial: error.message || 'Failed to load financial data' }));
      toast.error('Failed to load financial data');
    }
  }, [selectedAccount]);

  // Fetch objectives data
  const fetchObjectives = useCallback(async () => {
    try {
      const params = selectedAccount !== 'all' ? { account: selectedAccount } : {};
      const response = await apiClient.get('/project-performance/objectives', {
        params,
        timeout: 300000
      });
      if (response.data.success) {
        setObjectivesData(response.data.data);
        setErrors(prev => ({ ...prev, objectives: null }));
      }
    } catch (error) {
      console.error('Error fetching objectives:', error);
      setErrors(prev => ({ ...prev, objectives: error.message || 'Failed to load objectives data' }));
      toast.error('Failed to load objectives data');
    }
  }, [selectedAccount]);

  // Fetch team data
  const fetchTeam = useCallback(async () => {
    try {
      const params = selectedAccount !== 'all' ? { account: selectedAccount } : {};
      const response = await apiClient.get('/project-performance/team', {
        params,
        timeout: 300000
      });
      if (response.data.success) {
        setTeamData(response.data.data);
        setErrors(prev => ({ ...prev, team: null }));
      }
    } catch (error) {
      console.error('Error fetching team:', error);
      setErrors(prev => ({ ...prev, team: error.message || 'Failed to load team data' }));
      toast.error('Failed to load team data');
    }
  }, [selectedAccount]);

  // Fetch queue data
  const fetchQueue = useCallback(async () => {
    try {
      const params = selectedAccount !== 'all' ? { account: selectedAccount } : {};
      const response = await apiClient.get('/project-performance/queue', {
        params,
        timeout: 300000
      });
      if (response.data.success) {
        setQueueData(response.data.data);
        setErrors(prev => ({ ...prev, queue: null }));
      }
    } catch (error) {
      console.error('Error fetching queue:', error);
      setErrors(prev => ({ ...prev, queue: error.message || 'Failed to load queue data' }));
      toast.error('Failed to load queue data');
    }
  }, [selectedAccount]);

  // Fetch all data
  const fetchAllData = useCallback(async (showRefreshing = false) => {
    if (showRefreshing) {
      setRefreshing(true);
    } else {
      setLoading(true);
    }

    try {
      await Promise.all([
        fetchOverview(),
        fetchFunnel(),
        fetchFinancial(),
        fetchObjectives(),
        fetchTeam(),
        fetchQueue()
      ]);
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  }, [fetchOverview, fetchFunnel, fetchFinancial, fetchObjectives, fetchTeam, fetchQueue, selectedAccount]);

  // Initial load
  useEffect(() => {
    fetchAccounts();
  }, [fetchAccounts]);

  useEffect(() => {
    fetchAllData();
  }, [fetchAllData]);

  // Handle account change
  const handleAccountChange = (e) => {
    setSelectedAccount(e.target.value);
  };

  // Handle refresh
  const handleRefresh = () => {
    fetchAllData(true);
    toast.success('Refreshing data...');
  };

  if (loading) {
    return (
      <div className="dashboard-layout">
        <Sidebar isOpen={sidebarOpen} toggleSidebar={() => setSidebarOpen(!sidebarOpen)} />
        <div className="project-performance" style={{ marginLeft: sidebarOpen ? '320px' : '80px' }}>
          <div className="loading-container">
            <Loader className="spinning" size={24} />
            <p>Loading project performance data...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-layout">
      <Sidebar isOpen={sidebarOpen} toggleSidebar={() => setSidebarOpen(!sidebarOpen)} />
      <div className="project-performance" style={{ marginLeft: sidebarOpen ? '320px' : '80px' }}>
        <div className="project-performance-container">
          <div className="project-performance-header">
            <div className="header-content">
              <div className="header-left">
                <button
                  className="header-menu-toggle"
                  onClick={() => setSidebarOpen(!sidebarOpen)}
                >
                  <Menu size={20} />
                </button>
                <div>
                  <h1 className="page-title">Project Performance Dashboard</h1>
                  <p className="page-subtitle">Monitor project health, metrics, and performance analytics</p>
                </div>
              </div>
              <div className="header-actions">
                <div className="header-user-profile">
                  <div className="user-profile">
                    <div className="user-avatar">
                      {(user?.email || 'U').charAt(0).toUpperCase()}
                    </div>
                    <span className="user-name">{user?.email || 'User'}</span>
                    <button className="logout-btn" onClick={logout} title="Logout">
                      <LogOut size={18} />
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <div className="project-performance-content">
            {/* Filters and Tabs */}
            <div className="dashboard-filters-tabs">
              <div className="filter-section">
                <label htmlFor="account-filter">Filter by Account:</label>
                <select
                  id="account-filter"
                  value={selectedAccount}
                  onChange={handleAccountChange}
                  className="account-filter-select"
                >
                  <option value="all">All Accounts</option>
                  {accounts.map(account => (
                    <option key={account} value={account}>{account}</option>
                  ))}
                </select>
              </div>
              <div className="tabs-section">
                <div className="dashboard-tabs">
                  <button
                    className={`tab-button ${activeTab === 'overview' ? 'active' : ''}`}
                    onClick={() => setActiveTab('overview')}
                  >
                    Overview
                  </button>
              <button
                className={`tab-button ${activeTab === 'funnel' ? 'active' : ''}`}
                onClick={() => setActiveTab('funnel')}
              >
                Funnel
              </button>
              <button
                className={`tab-button ${activeTab === 'financial' ? 'active' : ''}`}
                onClick={() => setActiveTab('financial')}
              >
                Financial
              </button>
              <button
                className={`tab-button ${activeTab === 'objectives' ? 'active' : ''}`}
                onClick={() => setActiveTab('objectives')}
              >
                Objectives
              </button>
              <button
                className={`tab-button ${activeTab === 'team' ? 'active' : ''}`}
                onClick={() => setActiveTab('team')}
              >
                Team
              </button>
              <button
                className={`tab-button ${activeTab === 'queue' ? 'active' : ''}`}
                onClick={() => setActiveTab('queue')}
              >
                Queue
              </button>
                </div>
                <button 
                  className="refresh-btn-top" 
                  onClick={handleRefresh}
                  disabled={refreshing}
                >
                  <RefreshCw size={16} className={refreshing ? 'spinning' : ''} />
                  {refreshing ? 'Refreshing...' : 'Refresh'}
                </button>
              </div>
            </div>

            {/* Tab Content */}
            <div className="tab-content">
              {activeTab === 'overview' && (
                <OverviewCards 
                  data={overviewData} 
                  error={errors.overview}
                  onRefresh={fetchOverview}
                />
              )}

              {activeTab === 'funnel' && (
                <FunnelChart 
                  data={funnelData} 
                  error={errors.funnel}
                  onRefresh={fetchFunnel}
                />
              )}

              {activeTab === 'financial' && (
                <FinancialChart 
                  data={financialData} 
                  error={errors.financial}
                  onRefresh={fetchFinancial}
                />
              )}

              {activeTab === 'objectives' && (
                <ObjectiveChart 
                  data={objectivesData} 
                  error={errors.objectives}
                  onRefresh={fetchObjectives}
                />
              )}

              {activeTab === 'team' && (
                <TeamChart 
                  data={teamData} 
                  error={errors.team}
                  onRefresh={fetchTeam}
                />
              )}

              {activeTab === 'queue' && (
                <QueueChart 
                  data={queueData} 
                  error={errors.queue}
                  onRefresh={fetchQueue}
                />
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProjectPerformance;
