import React, { useState, useEffect, useMemo } from 'react';
import { X, Plus, Trash2, Search } from 'lucide-react';

const POProductivityTargetsFilterBuilder = ({ availableFields, records = [], filters, onSubmit, onClear, onClose }) => {
  const [localFilters, setLocalFilters] = useState(filters);
  const [valueSearchTerms, setValueSearchTerms] = useState({});

  useEffect(() => {
    setLocalFilters(filters);
  }, [filters]);

  const addFilter = () => {
    setLocalFilters({ ...localFilters, [Date.now().toString()]: { field: '', value: '' } });
  };

  const removeFilter = (key) => {
    const updated = { ...localFilters };
    delete updated[key];
    setLocalFilters(updated);
    const updatedSearchTerms = { ...valueSearchTerms };
    delete updatedSearchTerms[key];
    setValueSearchTerms(updatedSearchTerms);
  };

  const updateFilter = (key, updates) => {
    setLocalFilters({ ...localFilters, [key]: { ...localFilters[key], ...updates } });
    // Clear value when field changes
    if (updates.field && updates.field !== localFilters[key]?.field) {
      setLocalFilters({ ...localFilters, [key]: { ...localFilters[key], field: updates.field, value: '' } });
    }
  };

  const handleSubmit = () => {
    // Convert to object format expected by backend: { fieldName: value }
    const filterObject = {};
    Object.keys(localFilters).forEach(key => {
      const filter = localFilters[key];
      if (filter.field && filter.value !== '' && filter.value !== null && filter.value !== undefined) {
        filterObject[filter.field] = filter.value;
      }
    });
    onSubmit(filterObject);
  };

  // Filter out deprecated fields and only show fields from the table (availableFields)
  const filteredFields = availableFields.filter(f => {
    const fieldLabel = (f.label || '').toLowerCase();
    return !f.deprecated && !fieldLabel.includes('deprecated');
  });

  // Extract unique values for a given field from records
  const getUniqueValuesForField = (fieldName) => {
    if (!fieldName || !records || records.length === 0) return [];

    const values = new Set();
    records.forEach(record => {
      const value = record[fieldName];
      if (value !== null && value !== undefined && value !== '') {
        // Convert to string for comparison, but preserve original type
        values.add(value);
      }
    });

    // Sort values appropriately
    const sortedValues = Array.from(values).sort((a, b) => {
      if (typeof a === 'number' && typeof b === 'number') {
        return a - b;
      }
      return String(a).localeCompare(String(b));
    });

    return sortedValues;
  };

  // Get field type to determine input type
  const getFieldType = (fieldName) => {
    const field = filteredFields.find(f => f.name === fieldName);
    if (!field) return 'text';

    const type = (field.type || '').toLowerCase();
    if (type === 'boolean' || type === 'checkbox') return 'boolean';
    if (type === 'date' || type === 'datetime') return 'date';
    if (type === 'int' || type === 'double' || type === 'currency' || type === 'percent') return 'number';
    if (type === 'picklist') return 'picklist';
    return 'text';
  };

  return (
    <div className="case-filter-builder">
      <div className="filter-builder-header">
        <h3>Filter PO Productivity Targets</h3>
        <button className="btn-close-filter" onClick={onClose}>
          <X size={18} />
        </button>
      </div>

      <div className="filter-builder-content">
        {Object.keys(localFilters).length === 0 ? (
          <div className="filter-empty-state">
            <p>No filters applied. Click "Add Filter" to filter the data.</p>
          </div>
        ) : (
          Object.keys(localFilters).map((key) => {
            const filter = localFilters[key];
            const field = filteredFields.find(f => f.name === filter.field);
            const fieldType = getFieldType(filter.field);
            const uniqueValues = filter.field ? getUniqueValuesForField(filter.field) : [];
            const valueSearchTerm = valueSearchTerms[key] || '';
            const filteredValues = uniqueValues.filter(v => 
              String(v).toLowerCase().includes(valueSearchTerm.toLowerCase())
            );

            return (
              <div key={key} className="filter-row">
                <select
                  className="filter-field-select"
                  value={filter.field || ''}
                  onChange={(e) => updateFilter(key, { field: e.target.value })}
                >
                  <option value="">Select Field</option>
                  {filteredFields.map(f => (
                    <option key={f.name} value={f.name}>
                      {f.label || f.name}
                    </option>
                  ))}
                </select>

                {/* Value input based on field type */}
                {!filter.field ? (
                  <div className="filter-value-select" style={{ padding: '8px 12px', border: '1px solid #d1d5db', borderRadius: '6px', fontSize: '13px', fontFamily: 'Poppins', background: '#f3f4f6', color: '#9ca3af', width: '100%', boxSizing: 'border-box', cursor: 'not-allowed' }}>
                    Select field first
                  </div>
                ) : fieldType === 'boolean' ? (
                  <select
                    className="filter-value-select"
                    value={filter.value === true ? 'true' : filter.value === false ? 'false' : ''}
                    onChange={(e) => updateFilter(key, { value: e.target.value === 'true' })}
                    style={{ padding: '8px 12px', border: '1px solid #d1d5db', borderRadius: '6px', fontSize: '13px', fontFamily: 'Poppins', background: '#fff', color: '#002329', width: '100%', boxSizing: 'border-box' }}
                  >
                    <option value="">Select Value</option>
                    <option value="true">True</option>
                    <option value="false">False</option>
                  </select>
                ) : fieldType === 'number' ? (
                  <input
                    type="number"
                    className="filter-value-select"
                    value={filter.value || ''}
                    onChange={(e) => updateFilter(key, { value: e.target.value ? parseFloat(e.target.value) : '' })}
                    placeholder="Enter number"
                    style={{ padding: '8px 12px', border: '1px solid #d1d5db', borderRadius: '6px', fontSize: '13px', fontFamily: 'Poppins', background: '#fff', color: '#002329', width: '100%', boxSizing: 'border-box' }}
                  />
                ) : fieldType === 'date' ? (
                  <input
                    type="date"
                    className="filter-value-select"
                    value={filter.value || ''}
                    onChange={(e) => updateFilter(key, { value: e.target.value })}
                    style={{ padding: '8px 12px', border: '1px solid #d1d5db', borderRadius: '6px', fontSize: '13px', fontFamily: 'Poppins', background: '#fff', color: '#002329', width: '100%', boxSizing: 'border-box' }}
                  />
                ) : uniqueValues.length > 0 ? (
                  <div style={{ position: 'relative', width: '100%' }}>
                    <div style={{ position: 'relative' }}>
                      <Search size={14} style={{ position: 'absolute', left: '8px', top: '50%', transform: 'translateY(-50%)', color: '#6b7280', pointerEvents: 'none', zIndex: 1 }} />
                      <input
                        type="text"
                        placeholder="Search values..."
                        value={valueSearchTerm}
                        onChange={(e) => setValueSearchTerms({ ...valueSearchTerms, [key]: e.target.value })}
                        style={{ padding: '8px 12px 8px 32px', border: '1px solid #d1d5db', borderRadius: '6px', fontSize: '13px', fontFamily: 'Poppins', background: '#fff', color: '#002329', width: '100%', boxSizing: 'border-box' }}
                      />
                    </div>
                    {filteredValues.length > 0 && (
                      <select
                        className="filter-value-select"
                        value={filter.value || ''}
                        onChange={(e) => updateFilter(key, { value: e.target.value })}
                        style={{ marginTop: '4px', padding: '8px 12px', border: '1px solid #d1d5db', borderRadius: '6px', fontSize: '13px', fontFamily: 'Poppins', background: '#fff', color: '#002329', width: '100%', boxSizing: 'border-box' }}
                      >
                        <option value="">Select Value ({filteredValues.length} available)</option>
                        {filteredValues.slice(0, 100).map((val, idx) => (
                          <option key={idx} value={val}>
                            {String(val)}
                          </option>
                        ))}
                        {filteredValues.length > 100 && (
                          <option disabled>... and {filteredValues.length - 100} more (refine search)</option>
                        )}
                      </select>
                    )}
                    {filteredValues.length === 0 && valueSearchTerm && (
                      <div style={{ marginTop: '4px', padding: '8px', fontSize: '12px', color: '#6b7280', fontStyle: 'italic' }}>
                        No values found matching "{valueSearchTerm}"
                      </div>
                    )}
                  </div>
                ) : (
                  <input
                    type="text"
                    className="filter-value-select"
                    value={filter.value || ''}
                    onChange={(e) => updateFilter(key, { value: e.target.value })}
                    placeholder="Enter value"
                    style={{ padding: '8px 12px', border: '1px solid #d1d5db', borderRadius: '6px', fontSize: '13px', fontFamily: 'Poppins', background: '#fff', color: '#002329', width: '100%', boxSizing: 'border-box' }}
                  />
                )}

                <button
                  className="filter-remove-btn"
                  onClick={() => removeFilter(key)}
                  title="Remove filter"
                >
                  <Trash2 size={14} />
                </button>
              </div>
            );
          })
        )}
      </div>

      <div className="filter-builder-actions">
        <button className="btn-add-filter" onClick={addFilter}>
          <Plus size={14} /> Add Filter
        </button>
        {Object.keys(localFilters).length > 0 && (
          <button className="btn-clear-filters" onClick={onClear}>
            <Trash2 size={14} /> Clear All
          </button>
        )}
        <button className="btn-submit-filters" onClick={handleSubmit}>
          Apply Filters
        </button>
      </div>
    </div>
  );
};

export default POProductivityTargetsFilterBuilder;
