import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import apiClient from '../config/api';
import toast from 'react-hot-toast';
import { getErrorMessage, handleError } from '../utils/errorHandler';
import { sanitizeObject } from '../utils/security';
import { Search, Info, Menu, Send, LogOut, Eye, Loader, X } from 'lucide-react';
import { useAuth } from '../context/AuthContext';
import Sidebar from '../components/Sidebar';
import PreviewModal from '../components/PreviewModal';
import ConfirmModal from '../components/ConfirmModal';
import useSidebarWidth from '../hooks/useSidebarWidth';
import BookmarkButton from '../components/BookmarkButton';
import '../styles/ProjectSetup.css';
import '../styles/Sidebar.css';
import '../styles/GlobalHeader.css';

const AddContributorReview = () => {
  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const { register, handleSubmit, setValue, watch, formState: { errors }, reset } = useForm();
  const [loading, setLoading] = useState(false);
  const [publishing, setPublishing] = useState(false);
  const [showPreviewModal, setShowPreviewModal] = useState(false);
  const [showPublishConfirm, setShowPublishConfirm] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const sidebarWidth = useSidebarWidth(sidebarOpen);

  // Lookup states
  const [contributors, setContributors] = useState([]);
  const [loadingContributors, setLoadingContributors] = useState(false);
  const [contributorSearchTerm, setContributorSearchTerm] = useState('');
  const [showContributorDropdown, setShowContributorDropdown] = useState(false);
  const [selectedContributor, setSelectedContributor] = useState(null);

  const [projects, setProjects] = useState([]);
  const [loadingProjects, setLoadingProjects] = useState(false);
  const [projectSearchTerm, setProjectSearchTerm] = useState('');
  const [showProjectDropdown, setShowProjectDropdown] = useState(false);
  const [selectedProject, setSelectedProject] = useState(null);

  const [projectObjectives, setProjectObjectives] = useState([]);
  const [loadingProjectObjectives, setLoadingProjectObjectives] = useState(false);
  const [projectObjectiveSearchTerm, setProjectObjectiveSearchTerm] = useState('');
  const [showProjectObjectiveDropdown, setShowProjectObjectiveDropdown] = useState(false);
  const [selectedProjectObjective, setSelectedProjectObjective] = useState(null);

  const [reviewers, setReviewers] = useState([]);
  const [loadingReviewers, setLoadingReviewers] = useState(false);
  const [reviewerSearchTerm, setReviewerSearchTerm] = useState('');
  const [showReviewerDropdown, setShowReviewerDropdown] = useState(false);
  const [selectedReviewer, setSelectedReviewer] = useState(null);

  // Reviewed field options
  const reviewedFieldOptions = ['--None--', 'Yes', 'No'];

  // Review Status options (common values)
  const reviewStatusOptions = [
    '--None--',
    'Pending',
    'In Progress',
    'Completed',
    'Approved',
    'Rejected',
    'Draft'
  ];

  // Search Contributors
  useEffect(() => {
    const searchContributors = async () => {
      if (!contributorSearchTerm || contributorSearchTerm.trim() === '') {
        setContributors([]);
        return;
      }

      setLoadingContributors(true);
      try {
        const response = await apiClient.get(`/salesforce/search-contacts?search=${encodeURIComponent(contributorSearchTerm)}`);
        if (response.data.success) {
          setContributors(response.data.contacts || []);
          setShowContributorDropdown(true);
        }
      } catch (error) {
        handleError(error, 'AddContributorReview - searchContributors');
        setContributors([]);
      } finally {
        setLoadingContributors(false);
      }
    };

    const timeoutId = setTimeout(() => {
      searchContributors();
    }, 300);

    return () => clearTimeout(timeoutId);
  }, [contributorSearchTerm]);

  // Search Projects
  useEffect(() => {
    const searchProjects = async () => {
      if (!projectSearchTerm || projectSearchTerm.trim() === '') {
        setProjects([]);
        return;
      }

      setLoadingProjects(true);
      try {
        const response = await apiClient.get(`/salesforce/search-projects?search=${encodeURIComponent(projectSearchTerm)}`);
        if (response.data.success) {
          setProjects(response.data.projects || []);
          setShowProjectDropdown(true);
        }
      } catch (error) {
        handleError(error, 'AddContributorReview - searchProjects');
        setProjects([]);
      } finally {
        setLoadingProjects(false);
      }
    };

    const timeoutId = setTimeout(() => {
      searchProjects();
    }, 300);

    return () => clearTimeout(timeoutId);
  }, [projectSearchTerm]);

  // Search Project Objectives
  useEffect(() => {
    const searchProjectObjectives = async () => {
      if (!projectObjectiveSearchTerm || projectObjectiveSearchTerm.trim() === '') {
        setProjectObjectives([]);
        return;
      }

      setLoadingProjectObjectives(true);
      try {
        const response = await apiClient.get(`/salesforce/search-project-objectives?search=${encodeURIComponent(projectObjectiveSearchTerm)}`);
        if (response.data.success) {
          setProjectObjectives(response.data.projectObjectives || []);
          setShowProjectObjectiveDropdown(true);
        }
      } catch (error) {
        handleError(error, 'AddContributorReview - searchProjectObjectives');
        setProjectObjectives([]);
      } finally {
        setLoadingProjectObjectives(false);
      }
    };

    const timeoutId = setTimeout(() => {
      searchProjectObjectives();
    }, 300);

    return () => clearTimeout(timeoutId);
  }, [projectObjectiveSearchTerm]);

  // Search Reviewers (Users)
  useEffect(() => {
    const searchReviewers = async () => {
      if (!reviewerSearchTerm || reviewerSearchTerm.trim() === '') {
        setReviewers([]);
        return;
      }

      setLoadingReviewers(true);
      try {
        const response = await apiClient.get(`/salesforce/search-users?search=${encodeURIComponent(reviewerSearchTerm)}`);
        if (response.data.success) {
          setReviewers(response.data.users || []);
          setShowReviewerDropdown(true);
        }
      } catch (error) {
        handleError(error, 'AddContributorReview - searchReviewers');
        setReviewers([]);
      } finally {
        setLoadingReviewers(false);
      }
    };

    const timeoutId = setTimeout(() => {
      searchReviewers();
    }, 300);

    return () => clearTimeout(timeoutId);
  }, [reviewerSearchTerm]);

  const handleContributorSelect = (contributor) => {
    setSelectedContributor(contributor);
    setValue('contributorId', contributor.Id);
    setValue('contributorName', contributor.Name);
    setContributorSearchTerm(contributor.Name);
    setShowContributorDropdown(false);
  };

  const handleProjectSelect = (project) => {
    setSelectedProject(project);
    setValue('projectId', project.Id);
    setValue('projectName', project.Name);
    setProjectSearchTerm(project.Name);
    setShowProjectDropdown(false);
  };

  const handleProjectObjectiveSelect = (projectObjective) => {
    setSelectedProjectObjective(projectObjective);
    setValue('projectObjectiveId', projectObjective.Id);
    setValue('projectObjectiveName', projectObjective.Name || projectObjective.Project_Objective_Name__c);
    setProjectObjectiveSearchTerm(projectObjective.Name || projectObjective.Project_Objective_Name__c);
    setShowProjectObjectiveDropdown(false);
  };

  const handleReviewerSelect = (reviewer) => {
    setSelectedReviewer(reviewer);
    setValue('reviewerId', reviewer.Id);
    setValue('reviewerName', reviewer.Name);
    setReviewerSearchTerm(reviewer.Name);
    setShowReviewerDropdown(false);
  };

  const handlePreview = () => {
    const formData = watch();
    setShowPreviewModal(true);
  };

  const handlePublish = async () => {
    setShowPublishConfirm(false);
    setPublishing(true);

    try {
      const formData = watch();
      const sanitizedData = sanitizeObject(formData);

      const response = await apiClient.post('/salesforce/create-contributor-review', sanitizedData);

      if (response.data.success) {
        toast.success('Contributor Review created successfully in Salesforce');
        // Reset form
        reset();
        setSelectedContributor(null);
        setSelectedProject(null);
        setSelectedProjectObjective(null);
        setSelectedReviewer(null);
        setContributorSearchTerm('');
        setProjectSearchTerm('');
        setProjectObjectiveSearchTerm('');
        setReviewerSearchTerm('');
      } else {
        toast.error(response.data.error || 'Failed to create Contributor Review');
      }
    } catch (error) {
      const errorMessage = getErrorMessage(error);
      toast.error(errorMessage || 'Failed to create Contributor Review');
      handleError(error, 'AddContributorReview - handlePublish');
    } finally {
      setPublishing(false);
    }
  };

  const formData = watch();

  return (
    <div className="dashboard-layout">
      <Sidebar isOpen={sidebarOpen} toggleSidebar={() => setSidebarOpen(!sidebarOpen)} />
      <div style={{ marginLeft: sidebarOpen ? `${sidebarWidth}px` : '80px', transition: 'margin-left 0.3s ease' }}>
        <div className="global-header">
          <div className="header-content">
            <div className="header-left">
              <button 
                className="header-menu-toggle"
                onClick={() => setSidebarOpen(!sidebarOpen)}
                aria-label="Toggle sidebar"
              >
                <Menu size={20} />
              </button>
              <h1 className="header-title">Add Contributor Review</h1>
            </div>
            <div className="header-right">
              <BookmarkButton pagePath="/add-contributor-review" pageName="Add Contributor Review" />
              <button
                className="header-icon-button"
                onClick={logout}
                title="Logout"
                aria-label="Logout"
              >
                <LogOut size={18} />
              </button>
            </div>
          </div>
        </div>

        <div className="project-setup-container" style={{ padding: '24px', maxWidth: '1200px', margin: '0 auto' }}>
          <form onSubmit={handleSubmit(() => setShowPublishConfirm(true))}>
            {/* Contributor Field */}
            <div className="form-section" style={{ marginBottom: '24px' }}>
              <label className="form-label">
                Contributor <span style={{ color: '#dc2626' }}>*</span>
              </label>
              <div style={{ position: 'relative' }}>
                <div style={{ position: 'relative' }}>
                  <Search size={16} style={{ position: 'absolute', left: '12px', top: '50%', transform: 'translateY(-50%)', color: '#666', zIndex: 1 }} />
                  <input
                    type="text"
                    placeholder="Search for contributor..."
                    value={contributorSearchTerm}
                    onChange={(e) => {
                      setContributorSearchTerm(e.target.value);
                      setShowContributorDropdown(true);
                    }}
                    onFocus={() => setShowContributorDropdown(true)}
                    style={{
                      width: '100%',
                      padding: '10px 12px 10px 36px',
                      border: errors.contributorId ? '1px solid #dc2626' : '1px solid #d8dde6',
                      borderRadius: '6px',
                      fontSize: '14px'
                    }}
                  />
                </div>
                {showContributorDropdown && contributors.length > 0 && (
                  <div style={{
                    position: 'absolute',
                    top: '100%',
                    left: 0,
                    right: 0,
                    background: '#fff',
                    border: '1px solid #d8dde6',
                    borderRadius: '6px',
                    marginTop: '4px',
                    maxHeight: '200px',
                    overflowY: 'auto',
                    zIndex: 1000,
                    boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
                  }}>
                    {loadingContributors ? (
                      <div style={{ padding: '12px', textAlign: 'center' }}>
                        <Loader size={16} className="spinning" style={{ color: '#08979C' }} />
                      </div>
                    ) : (
                      contributors.map((contributor) => (
                        <div
                          key={contributor.Id}
                          onClick={() => handleContributorSelect(contributor)}
                          style={{
                            padding: '10px 12px',
                            cursor: 'pointer',
                            borderBottom: '1px solid #f0f0f0'
                          }}
                          onMouseEnter={(e) => e.target.style.backgroundColor = '#f5f5f5'}
                          onMouseLeave={(e) => e.target.style.backgroundColor = '#fff'}
                        >
                          {contributor.Name}
                        </div>
                      ))
                    )}
                  </div>
                )}
                <input type="hidden" {...register('contributorId', { required: 'Contributor is required' })} />
                <input type="hidden" {...register('contributorName')} />
              </div>
              {errors.contributorId && (
                <span style={{ color: '#dc2626', fontSize: '12px', marginTop: '4px', display: 'block' }}>
                  {errors.contributorId.message}
                </span>
              )}
            </div>

            {/* Project Field */}
            <div className="form-section" style={{ marginBottom: '24px' }}>
              <label className="form-label">Project</label>
              <div style={{ position: 'relative' }}>
                <div style={{ position: 'relative' }}>
                  <Search size={16} style={{ position: 'absolute', left: '12px', top: '50%', transform: 'translateY(-50%)', color: '#666', zIndex: 1 }} />
                  <input
                    type="text"
                    placeholder="Search for project..."
                    value={projectSearchTerm}
                    onChange={(e) => {
                      setProjectSearchTerm(e.target.value);
                      setShowProjectDropdown(true);
                    }}
                    onFocus={() => setShowProjectDropdown(true)}
                    style={{
                      width: '100%',
                      padding: '10px 12px 10px 36px',
                      border: '1px solid #d8dde6',
                      borderRadius: '6px',
                      fontSize: '14px'
                    }}
                  />
                </div>
                {showProjectDropdown && projects.length > 0 && (
                  <div style={{
                    position: 'absolute',
                    top: '100%',
                    left: 0,
                    right: 0,
                    background: '#fff',
                    border: '1px solid #d8dde6',
                    borderRadius: '6px',
                    marginTop: '4px',
                    maxHeight: '200px',
                    overflowY: 'auto',
                    zIndex: 1000,
                    boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
                  }}>
                    {loadingProjects ? (
                      <div style={{ padding: '12px', textAlign: 'center' }}>
                        <Loader size={16} className="spinning" style={{ color: '#08979C' }} />
                      </div>
                    ) : (
                      projects.map((project) => (
                        <div
                          key={project.Id}
                          onClick={() => handleProjectSelect(project)}
                          style={{
                            padding: '10px 12px',
                            cursor: 'pointer',
                            borderBottom: '1px solid #f0f0f0'
                          }}
                          onMouseEnter={(e) => e.target.style.backgroundColor = '#f5f5f5'}
                          onMouseLeave={(e) => e.target.style.backgroundColor = '#fff'}
                        >
                          {project.Name}
                        </div>
                      ))
                    )}
                  </div>
                )}
                <input type="hidden" {...register('projectId')} />
                <input type="hidden" {...register('projectName')} />
              </div>
            </div>

            {/* Project Objective Field */}
            <div className="form-section" style={{ marginBottom: '24px' }}>
              <label className="form-label">Project Objective</label>
              <div style={{ position: 'relative' }}>
                <div style={{ position: 'relative' }}>
                  <Search size={16} style={{ position: 'absolute', left: '12px', top: '50%', transform: 'translateY(-50%)', color: '#666', zIndex: 1 }} />
                  <input
                    type="text"
                    placeholder="Search for project objective..."
                    value={projectObjectiveSearchTerm}
                    onChange={(e) => {
                      setProjectObjectiveSearchTerm(e.target.value);
                      setShowProjectObjectiveDropdown(true);
                    }}
                    onFocus={() => setShowProjectObjectiveDropdown(true)}
                    style={{
                      width: '100%',
                      padding: '10px 12px 10px 36px',
                      border: '1px solid #d8dde6',
                      borderRadius: '6px',
                      fontSize: '14px'
                    }}
                  />
                </div>
                {showProjectObjectiveDropdown && projectObjectives.length > 0 && (
                  <div style={{
                    position: 'absolute',
                    top: '100%',
                    left: 0,
                    right: 0,
                    background: '#fff',
                    border: '1px solid #d8dde6',
                    borderRadius: '6px',
                    marginTop: '4px',
                    maxHeight: '200px',
                    overflowY: 'auto',
                    zIndex: 1000,
                    boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
                  }}>
                    {loadingProjectObjectives ? (
                      <div style={{ padding: '12px', textAlign: 'center' }}>
                        <Loader size={16} className="spinning" style={{ color: '#08979C' }} />
                      </div>
                    ) : (
                      projectObjectives.map((po) => (
                        <div
                          key={po.Id}
                          onClick={() => handleProjectObjectiveSelect(po)}
                          style={{
                            padding: '10px 12px',
                            cursor: 'pointer',
                            borderBottom: '1px solid #f0f0f0'
                          }}
                          onMouseEnter={(e) => e.target.style.backgroundColor = '#f5f5f5'}
                          onMouseLeave={(e) => e.target.style.backgroundColor = '#fff'}
                        >
                          {po.Name || po.Project_Objective_Name__c}
                        </div>
                      ))
                    )}
                  </div>
                )}
                <input type="hidden" {...register('projectObjectiveId')} />
                <input type="hidden" {...register('projectObjectiveName')} />
              </div>
            </div>

            {/* Review Date */}
            <div className="form-section" style={{ marginBottom: '24px' }}>
              <label className="form-label">
                Review Date <span style={{ color: '#dc2626' }}>*</span>
              </label>
              <input
                type="date"
                {...register('reviewDate', { required: 'Review Date is required' })}
                style={{
                  width: '100%',
                  padding: '10px 12px',
                  border: errors.reviewDate ? '1px solid #dc2626' : '1px solid #d8dde6',
                  borderRadius: '6px',
                  fontSize: '14px'
                }}
              />
              {errors.reviewDate && (
                <span style={{ color: '#dc2626', fontSize: '12px', marginTop: '4px', display: 'block' }}>
                  {errors.reviewDate.message}
                </span>
              )}
            </div>

            {/* Reviewed Fields - Using Yes/No/--None-- */}
            <div className="form-section" style={{ marginBottom: '24px' }}>
              <h3 style={{ fontSize: '16px', fontWeight: '600', marginBottom: '16px', color: '#333' }}>Reviewed Fields</h3>

              {/* Reviewed Field 1 */}
              <div style={{ marginBottom: '16px' }}>
                <label className="form-label">Reviewed Field 1</label>
                <select
                  {...register('reviewedField1')}
                  style={{
                    width: '100%',
                    padding: '10px 12px',
                    border: '1px solid #d8dde6',
                    borderRadius: '6px',
                    fontSize: '14px'
                  }}
                >
                  {reviewedFieldOptions.map(option => (
                    <option key={option} value={option}>{option}</option>
                  ))}
                </select>
              </div>

              {/* Reviewed Field 2 */}
              <div style={{ marginBottom: '16px' }}>
                <label className="form-label">Reviewed Field 2</label>
                <select
                  {...register('reviewedField2')}
                  style={{
                    width: '100%',
                    padding: '10px 12px',
                    border: '1px solid #d8dde6',
                    borderRadius: '6px',
                    fontSize: '14px'
                  }}
                >
                  {reviewedFieldOptions.map(option => (
                    <option key={option} value={option}>{option}</option>
                  ))}
                </select>
              </div>

              {/* Reviewed Field 3 */}
              <div style={{ marginBottom: '16px' }}>
                <label className="form-label">Reviewed Field 3</label>
                <select
                  {...register('reviewedField3')}
                  style={{
                    width: '100%',
                    padding: '10px 12px',
                    border: '1px solid #d8dde6',
                    borderRadius: '6px',
                    fontSize: '14px'
                  }}
                >
                  {reviewedFieldOptions.map(option => (
                    <option key={option} value={option}>{option}</option>
                  ))}
                </select>
              </div>

              {/* Reviewed Field 4 */}
              <div style={{ marginBottom: '16px' }}>
                <label className="form-label">Reviewed Field 4</label>
                <select
                  {...register('reviewedField4')}
                  style={{
                    width: '100%',
                    padding: '10px 12px',
                    border: '1px solid #d8dde6',
                    borderRadius: '6px',
                    fontSize: '14px'
                  }}
                >
                  {reviewedFieldOptions.map(option => (
                    <option key={option} value={option}>{option}</option>
                  ))}
                </select>
              </div>

              {/* Reviewed Field 5 */}
              <div style={{ marginBottom: '16px' }}>
                <label className="form-label">Reviewed Field 5</label>
                <select
                  {...register('reviewedField5')}
                  style={{
                    width: '100%',
                    padding: '10px 12px',
                    border: '1px solid #d8dde6',
                    borderRadius: '6px',
                    fontSize: '14px'
                  }}
                >
                  {reviewedFieldOptions.map(option => (
                    <option key={option} value={option}>{option}</option>
                  ))}
                </select>
              </div>
            </div>

            {/* Review Status */}
            <div className="form-section" style={{ marginBottom: '24px' }}>
              <label className="form-label">Review Status</label>
              <select
                {...register('reviewStatus')}
                style={{
                  width: '100%',
                  padding: '10px 12px',
                  border: '1px solid #d8dde6',
                  borderRadius: '6px',
                  fontSize: '14px'
                }}
              >
                {reviewStatusOptions.map(option => (
                  <option key={option} value={option}>{option}</option>
                ))}
              </select>
            </div>

            {/* Reviewer Field */}
            <div className="form-section" style={{ marginBottom: '24px' }}>
              <label className="form-label">Reviewer</label>
              <div style={{ position: 'relative' }}>
                <div style={{ position: 'relative' }}>
                  <Search size={16} style={{ position: 'absolute', left: '12px', top: '50%', transform: 'translateY(-50%)', color: '#666', zIndex: 1 }} />
                  <input
                    type="text"
                    placeholder="Search for reviewer..."
                    value={reviewerSearchTerm}
                    onChange={(e) => {
                      setReviewerSearchTerm(e.target.value);
                      setShowReviewerDropdown(true);
                    }}
                    onFocus={() => setShowReviewerDropdown(true)}
                    style={{
                      width: '100%',
                      padding: '10px 12px 10px 36px',
                      border: '1px solid #d8dde6',
                      borderRadius: '6px',
                      fontSize: '14px'
                    }}
                  />
                </div>
                {showReviewerDropdown && reviewers.length > 0 && (
                  <div style={{
                    position: 'absolute',
                    top: '100%',
                    left: 0,
                    right: 0,
                    background: '#fff',
                    border: '1px solid #d8dde6',
                    borderRadius: '6px',
                    marginTop: '4px',
                    maxHeight: '200px',
                    overflowY: 'auto',
                    zIndex: 1000,
                    boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
                  }}>
                    {loadingReviewers ? (
                      <div style={{ padding: '12px', textAlign: 'center' }}>
                        <Loader size={16} className="spinning" style={{ color: '#08979C' }} />
                      </div>
                    ) : (
                      reviewers.map((reviewer) => (
                        <div
                          key={reviewer.Id}
                          onClick={() => handleReviewerSelect(reviewer)}
                          style={{
                            padding: '10px 12px',
                            cursor: 'pointer',
                            borderBottom: '1px solid #f0f0f0'
                          }}
                          onMouseEnter={(e) => e.target.style.backgroundColor = '#f5f5f5'}
                          onMouseLeave={(e) => e.target.style.backgroundColor = '#fff'}
                        >
                          {reviewer.Name}
                        </div>
                      ))
                    )}
                  </div>
                )}
                <input type="hidden" {...register('reviewerId')} />
                <input type="hidden" {...register('reviewerName')} />
              </div>
            </div>

            {/* Comments */}
            <div className="form-section" style={{ marginBottom: '24px' }}>
              <label className="form-label">Comments</label>
              <textarea
                {...register('comments')}
                rows={4}
                placeholder="Enter comments..."
                style={{
                  width: '100%',
                  padding: '10px 12px',
                  border: '1px solid #d8dde6',
                  borderRadius: '6px',
                  fontSize: '14px',
                  fontFamily: 'inherit',
                  resize: 'vertical'
                }}
              />
            </div>

            {/* Action Buttons */}
            <div className="form-actions" style={{ marginTop: '24px', paddingTop: '16px', borderTop: '1px solid #d8dde6' }}>
              <div style={{ display: 'flex', gap: '12px', alignItems: 'center', width: '100%', justifyContent: 'flex-end' }}>
                <button 
                  type="button" 
                  className="btn-secondary" 
                  onClick={handlePreview}
                  style={{ display: 'flex', alignItems: 'center', gap: '6px' }}
                >
                  <Eye size={16} />
                  Preview
                </button>
                <button
                  type="submit"
                  className="btn-primary"
                  disabled={publishing}
                  style={{ display: 'flex', alignItems: 'center', gap: '6px' }}
                >
                  {publishing ? (
                    <>
                      <Loader size={16} className="spinning" style={{ color: '#08979C' }} />
                      Publishing...
                    </>
                  ) : (
                    <>
                      <Send size={16} />
                      Publish
                    </>
                  )}
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>

      {/* Preview Modal */}
      <PreviewModal
        show={showPreviewModal}
        onClose={() => setShowPreviewModal(false)}
        formData={formData}
        objectType="Contributor Review"
        displayObjectLabel="Contributor Review"
      />

      {/* Publish Confirmation Modal */}
      <ConfirmModal
        show={showPublishConfirm}
        message="Are you sure you want to publish this Contributor Review to Salesforce? This will create a new record."
        onConfirm={handlePublish}
        onCancel={() => setShowPublishConfirm(false)}
        confirmText="Publish"
        cancelText="Cancel"
        type="warning"
      />
    </div>
  );
};

export default AddContributorReview;
