                            ))
                          ) : (
                            <span style={{ color: '#999' }}>Click to select contributor projects...</span>
                          )}
                        </div>
                      ) : (
                        <>
                          <div style={{ position: 'relative', marginBottom: '4px' }}>
                            <Search size={14} style={{ position: 'absolute', left: '8px', top: '50%', transform: 'translateY(-50%)', color: '#666' }} />
                            <input
                              type="text"
                              placeholder="Search contributor projects..."
                              value={contributorProjectSearchTerm}
                              onChange={(e) => setContributorProjectSearchTerm(e.target.value)}
                              onFocus={() => setShowContributorProjectDropdown(true)}
                              style={{
                                width: '100%',
                                padding: '4px 8px 4px 28px',
                                border: '1px solid var(--border-color)',
                                borderRadius: '4px',
                                fontSize: '12px',
                                background: '#fff'
                              }}
                            />
                          </div>
                          <div style={{ maxHeight: '120px', overflowY: 'auto', border: '1px solid var(--border-color)', borderRadius: '4px', background: '#fff' }}>
                            {getFilteredItems(allContributorProjects, contributorProjectSearchTerm).slice(0, 50).map(cp => (
                              <label key={cp.id || cp.Id} style={{ display: 'flex', alignItems: 'center', gap: '6px', padding: '4px 8px', cursor: 'pointer', fontSize: '12px' }}>
                                <input
                                  type="checkbox"
                                  checked={formData.filters.contributorProjects.selected.includes(cp.id || cp.Id)}
                                  onChange={() => handleFilterSelect('contributorProjects', cp.id || cp.Id)}
                                  style={{ cursor: 'pointer' }}
                                />
                                {cp.name || cp.Name}
                              </label>
                            ))}
                          </div>
                        </>
                      )}
                    </div>
                  )}
                </div>
              </div>
            </div>

            {/* Action Buttons */}
            <div style={{ display: 'flex', gap: '8px', justifyContent: 'flex-end', marginTop: '8px' }}>
              <button
                onClick={() => {
                  setShowCreateForm(false);
                  setEditingRule(null);
                }}
                className="btn-secondary"
                style={standardButtonStyle}
              >
                Cancel
              </button>
              <button
                onClick={handleSave}
                className="btn-primary"
                style={standardButtonStyle}
              >
                <Save size={16} />
                {editingRule ? 'Update' : 'Create'}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Rules List */}
      {rules.length === 0 ? (
        <div style={{ padding: '40px', textAlign: 'center', color: '#666' }}>
          <Settings size={48} style={{ margin: '0 auto 16px', opacity: 0.5 }} />
          <p>No schedule rules configured</p>
          <button
            onClick={handleCreate}
            className="btn-primary"
            style={{ ...standardButtonStyle, margin: '16px auto 0' }}
          >
            <Plus size={16} />
            Create Your First Rule
          </button>
        </div>
      ) : (
        <div style={{ display: 'flex', flexDirection: 'column', gap: '16px' }}>
          {rules.map((rule) => (
            <div
              key={rule.id}
              style={{
                padding: '16px',
                background: rule.enabled ? '#f0fdf4' : 'var(--bg-secondary)',
                borderRadius: '6px',
                border: `1px solid ${rule.enabled ? '#86efac' : 'var(--border-color)'}`
              }}
            >
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                <div style={{ flex: 1 }}>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '8px' }}>
                    <h3 style={{ margin: 0, fontSize: '15px', fontWeight: '600' }}>{rule.name}</h3>
                    <span
                      style={{
                        padding: '2px 8px',
                        borderRadius: '4px',
                        fontSize: '11px',
                        fontWeight: '600',
                        background: rule.enabled ? '#22c55e' : '#9ca3af',
                        color: '#fff'
                      }}
                    >
                      {rule.enabled ? 'ENABLED' : 'DISABLED'}
                    </span>
                    <span
                      style={{
                        padding: '2px 8px',
                        borderRadius: '4px',
                        fontSize: '11px',
                        fontWeight: '500',
                        background: rule.type === 'time_based' ? '#dbeafe' : '#fef3c7',
                        color: rule.type === 'time_based' ? '#1e40af' : '#92400e'
                      }}
                    >
                      {rule.type === 'time_based' ? 'TIME-BASED' : 'CONDITION-BASED'}
                    </span>
                  </div>
                  <div style={{ fontSize: '13px', color: '#666', marginTop: '8px' }}>
                    <div><strong>From:</strong> {rule.fromStatus}</div>
                    <div><strong>To:</strong> {rule.toStatus}</div>
                    {rule.type === 'time_based' && (
                      <div>
                        <strong>After:</strong> {
                          rule.timeType === 'date' && rule.specificDate
                            ? new Date(rule.specificDate).toLocaleDateString()
                            : `${rule.days || 0} day(s)`
                        }
                      </div>
                    )}
                    {rule.type === 'condition_based' && rule.conditions && rule.conditions.length > 0 && (
                      <div><strong>Conditions:</strong> {rule.conditions.length} condition(s)</div>
                    )}
                    {rule.status === 'done' && (
                      <div style={{ marginTop: '4px', fontSize: '12px', color: '#22c55e', fontWeight: '500' }}>
                         Status: Done
                        {rule.lastExecutionCount && ` (Updated ${rule.lastExecutionCount} project(s))`}
                        {rule.lastExecutedAt && ` - ${new Date(rule.lastExecutedAt).toLocaleString()}`}
                      </div>
                    )}
                    {rule.filters && (
                      <div style={{ marginTop: '4px', fontSize: '12px' }}>
                        <strong>Filters:</strong>
                        {rule.filters.projects?.mode !== 'none' && rule.filters.projects.selected?.length > 0 && (
                          <span style={{ marginLeft: '4px' }}>
                            Projects ({rule.filters.projects.mode}): {getSelectedItems(allProjects, rule.filters.projects.selected).map(p => p.name || p.Name).join(', ')}
                          </span>
                        )}
                        {rule.filters.projectObjectives?.mode !== 'none' && rule.filters.projectObjectives.selected?.length > 0 && (
                          <span style={{ marginLeft: '4px' }}>
                            Objectives ({rule.filters.projectObjectives.mode}): {getSelectedItems(allProjectObjectives, rule.filters.projectObjectives.selected).map(po => po.name || po.Name || po.contributorFacingProjectName).join(', ')}
                          </span>
                        )}
                        {rule.filters.contributorProjects?.mode !== 'none' && rule.filters.contributorProjects.selected?.length > 0 && (
                          <span style={{ marginLeft: '4px' }}>
                            Contributor Projects ({rule.filters.contributorProjects.mode}): {getSelectedItems(allContributorProjects, rule.filters.contributorProjects.selected).map(cp => cp.contributorProjectName || cp.name || cp.Name).join(', ')}
                          </span>
                        )}
                      </div>
                    )}
                    {rule.createdAt && (
                      <div style={{ marginTop: '8px', fontSize: '12px', color: '#999' }}>
                        Created: {new Date(rule.createdAt).toLocaleDateString()}
                      </div>
                    )}
                  </div>
                </div>
                <div style={{ display: 'flex', gap: '8px', alignItems: 'flex-start' }}>
                  <button
                    onClick={() => handleToggleEnabled(rule)}
                    className="btn-secondary"
                    style={standardButtonStyle}
                    title={rule.enabled ? 'Disable rule' : 'Enable rule'}
                  >
                    {rule.enabled ? <Pause size={16} /> : <Play size={16} />}
                    {rule.enabled ? 'Disable' : 'Enable'}
                  </button>
                  <button
                    onClick={() => handleEdit(rule)}
                    className="btn-secondary"
                    style={standardButtonStyle}
                    title="Edit rule"
                  >
                    <Edit size={16} />
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(rule.id)}
                    className="btn-secondary"
                    style={{ ...standardButtonStyle, color: '#dc2626' }}
                    title="Delete rule"
                  >
                    <Trash2 size={16} />
                    Delete
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default QueueStatusScheduleManager;
