                  placeholder={`Search ${getObjectTypeLabel(selectedObjectType)} by name...`}
                  style={{
                    width: '100%',
                    padding: '12px 16px',
                    paddingRight: loadingSearch ? '40px' : '16px',
                    border: '1px solid #d8dde6',
                    borderRadius: '6px',
                    fontSize: '14px'
                  }}
                />
                {loadingSearch && (
                  <div style={{ position: 'absolute', right: '12px', top: '50%', transform: 'translateY(-50%)' }}>
                    <Loader size={16} className="spinning" />
                  </div>
                )}
              </div>

              {searchResults.length > 0 && (
                <div style={{
                  border: '1px solid #d8dde6',
                  borderRadius: '6px',
                  maxHeight: '400px',
                  overflowY: 'auto'
                }}>
                  {searchResults.map((result) => (
                    <div
                      key={result.id}
                      onClick={() => handleSelectObject(result)}
                      style={{
                        padding: '12px 16px',
                        borderBottom: '1px solid #f0f0f0',
                        cursor: 'pointer',
                        transition: 'background 0.2s'
                      }}
                      onMouseEnter={(e) => e.target.style.background = '#f5f5f5'}
                      onMouseLeave={(e) => e.target.style.background = '#fff'}
                    >
                      {result.name}
                    </div>
                  ))}
                </div>
              )}

              {loadingObjectData && (
                <div style={{ textAlign: 'center', padding: '40px' }}>
                  <Loader size={24} className="spinning" />
                  <p style={{ marginTop: '16px', color: '#666' }}>Loading object data...</p>
                </div>
              )}
            </div>
          )}

          {step === 'edit' && objectData && (
            <div>
              <div style={{ display: 'flex', alignItems: 'center', gap: '16px', marginBottom: '24px' }}>
                <button
                  onClick={() => {
                    setStep('search');
                    setSelectedObject(null);
                    setObjectData(null);
                    setOriginalFormData(null);
                    setHasChanges(false);
                    setTeamMembers([{ member: '', memberId: '', role: '' }]);
                    reset();
                  }}
                  style={{
                    padding: '8px 16px',
                    border: '1px solid #d8dde6',
                    borderRadius: '6px',
                    background: '#fff',
                    cursor: 'pointer'
                  }}
                >
                  Back
                </button>
                <h2 style={{ fontSize: '18px', fontWeight: 600 }}>
                  Edit {getObjectTypeLabel(selectedObjectType)}: {selectedObject?.name}
                </h2>
              </div>

              {selectedObjectType === 'project' && renderProjectForm()}

              {selectedObjectType !== 'project' && (
                <div style={{ marginBottom: '24px', padding: '16px', background: '#f5f5f5', borderRadius: '6px' }}>
                  <p style={{ margin: 0, fontSize: '14px', color: '#666' }}>
                    Form for {getObjectTypeLabel(selectedObjectType)} will be implemented here.
                  </p>
                </div>
              )}
            </div>
          )}
          </div>
        </div>
      </div>

      {showPreviewModal && (
        <PreviewModal
          isOpen={showPreviewModal}
          onClose={() => setShowPreviewModal(false)}
          objectType={selectedObjectType}
          formData={watch()}
          objectLabel={getObjectTypeLabel(selectedObjectType)}
          onEdit={() => {
            setShowPreviewModal(false);
          }}
        />
      )}

      <ConfirmModal
        show={showNoChangesConfirm}
        message="No changes have been made to the object. The object will be cloned without any modifications. Do you want to proceed?"
        onConfirm={doPublish}
        onCancel={() => setShowNoChangesConfirm(false)}
        confirmText="Yes, Clone"
        cancelText="Cancel"
        type="info"
      />
    </div>
  );
};

export default Clone;
