import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import apiClient from '../config/api';
import toast from 'react-hot-toast';
import { Search, Menu, LogOut, Send, Eye, Loader, X, CheckCircle, XCircle } from 'lucide-react';
import { useAuth } from '../context/AuthContext';
import Sidebar from '../components/Sidebar';
import PreviewModal from '../components/PreviewModal';
import ConfirmModal from '../components/ConfirmModal';
import '../styles/ProjectSetup.css';
import '../styles/Sidebar.css';
import '../styles/GlobalHeader.css';

const Clone = () => {
  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const { register, handleSubmit, setValue, watch, formState: { errors }, reset } = useForm();

  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [step, setStep] = useState('objectType'); // 'objectType', 'search', 'edit'
  const [objectTypes, setObjectTypes] = useState([]);
  const [selectedObjectType, setSelectedObjectType] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loadingSearch, setLoadingSearch] = useState(false);
  const [selectedObject, setSelectedObject] = useState(null);
  const [objectData, setObjectData] = useState(null);
  const [loadingObjectData, setLoadingObjectData] = useState(false);
  const [originalFormData, setOriginalFormData] = useState(null);
  const [showPreviewModal, setShowPreviewModal] = useState(false);
  const [saving, setSaving] = useState(false);
  const [publishing, setPublishing] = useState(false);
  const [showNoChangesConfirm, setShowNoChangesConfirm] = useState(false);
  const [hasChanges, setHasChanges] = useState(false);

  const searchTimeoutRef = useRef(null);
  const searchInputRef = useRef(null);

  // Load object types
  useEffect(() => {
    loadObjectTypes();
  }, []);

  // Watch form data for changes
  useEffect(() => {
    if (objectData && originalFormData) {
      const currentData = watch();
      const hasFormChanges = JSON.stringify(currentData) !== JSON.stringify(originalFormData);
      setHasChanges(hasFormChanges);
    }
  }, [watch(), objectData, originalFormData]);

  const loadObjectTypes = async () => {
    try {
      const response = await apiClient.get('/clone/object-types');
      if (response.data.success) {
        setObjectTypes(response.data.objectTypes);
      }
    } catch (error) {
      toast.error('Failed to load object types');
    }
  };

  const handleObjectTypeSelect = (objectType) => {
    setSelectedObjectType(objectType);
    setStep('search');
    setSearchTerm('');
    setSearchResults([]);
    setSelectedObject(null);
    setObjectData(null);
    setOriginalFormData(null);
    reset();
  };

  const handleSearch = async (term) => {
    setSearchTerm(term);

    if (!term || term.trim() === '') {
      setSearchResults([]);
      return;
    }

    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }

    setLoadingSearch(true);

    searchTimeoutRef.current = setTimeout(async () => {
      try {
        const response = await apiClient.post('/clone/search', {
          objectType: selectedObjectType,
          searchTerm: term
        });

        if (response.data.success) {
          setSearchResults(response.data.records);
        }
      } catch (error) {
        toast.error('Failed to search objects');
        setSearchResults([]);
      } finally {
        setLoadingSearch(false);
      }
    }, 300);
  };

  const handleSelectObject = async (object) => {
    setSelectedObject(object);
    setSearchResults([]);
    setLoadingObjectData(true);

    try {
      const response = await apiClient.get(`/clone/object/${selectedObjectType}/${object.id}`);

      if (response.data.success) {
        setObjectData(response.data);
        setOriginalFormData(response.data.formData);

        // Populate form with data
        Object.keys(response.data.formData).forEach(key => {
          setValue(key, response.data.formData[key]);
        });

        setStep('edit');
      }
    } catch (error) {
      toast.error('Failed to load object data');
    } finally {
      setLoadingObjectData(false);
    }
  };

  const handlePublish = async () => {
    if (!hasChanges) {
      setShowNoChangesConfirm(true);
      return;
    }

    await doPublish();
  };

  const doPublish = async () => {
    setPublishing(true);
    try {
      const formData = watch();

      const response = await apiClient.post('/clone/clone', {
        objectType: selectedObjectType,
        formData: formData,
        hasChanges: hasChanges
      });

      if (response.data.success) {
        toast.success('Object cloned successfully!');
        // Reset to start
        setStep('objectType');
        setSelectedObjectType('');
        setSearchTerm('');
        setSelectedObject(null);
        setObjectData(null);
        setOriginalFormData(null);
        setHasChanges(false);
        reset();
      }
    } catch (error) {
      toast.error(error.response?.data?.error || 'Failed to clone object');
    } finally {
      setPublishing(false);
      setShowNoChangesConfirm(false);
    }
  };

  const getObjectTypeLabel = (value) => {
    const obj = objectTypes.find(t => t.value === value);
    return obj ? obj.label : value;
  };

  // Organize fields into sections based on field name patterns
  const organizeFieldsIntoSections = (fields) => {
    if (!fields || fields.length === 0) return {};

    const sections = {
      'Information': [],
      'Contributor Active Status': [],
      'People': [],
      'Project Team': [],
      'Rates': [],
      'Funnel Totals': [],
      'Funnel Stages': [],
      'Lever Requisition Actions': [],
      'Lever Requisition Fields': [],
      'Lever Admin': [],
      'Payment Configurations': [],
      'Activation': [],
      'Other': []
    };

    fields.forEach(field => {
      if (field.calculated || field.autoNumber) return;

      const fieldName = field.name.toLowerCase();
      const fieldLabel = field.label.toLowerCase();

      // Information section
      if (fieldName.includes('name') || 
          fieldName.includes('description') || 
          fieldName.includes('short') ||
          fieldName.includes('contributor') ||
          fieldName.includes('partner') ||
          fieldName.includes('category') ||
          fieldName.includes('auditor') ||
          fieldName.includes('project') && (fieldName.includes('type') || fieldName.includes('priority') || fieldName.includes('status') || fieldName.includes('id')) ||
          fieldName.includes('account') ||
          fieldName.includes('program') ||
          fieldName.includes('delivery') ||
          fieldName.includes('tool') ||
          fieldName.includes('page') ||
          fieldName.includes('url') ||
          fieldName.includes('location') ||
          fieldName.includes('date') && !fieldName.includes('funnel') ||
          fieldLabel.includes('name') ||
          fieldLabel.includes('description') ||
          fieldLabel.includes('short')) {
        sections['Information'].push(field);
      }
      // Contributor Active Status
      else if (fieldName.includes('payment') && fieldName.includes('setup') ||
               fieldName.includes('manual') && fieldName.includes('activation') ||
               fieldName.includes('client') && fieldName.includes('tool') && fieldName.includes('account')) {
        sections['Contributor Active Status'].push(field);
      }
      // People section
      else if (fieldName.includes('manager') ||
               fieldName.includes('lead') ||
               fieldName.includes('support') ||
               fieldName.includes('people') ||
               fieldLabel.includes('manager') ||
               fieldLabel.includes('lead')) {
        sections['People'].push(field);
      }
      // Project Team
      else if (fieldName.includes('team') ||
               fieldName.includes('member')) {
        sections['Project Team'].push(field);
      }
      // Rates
      else if (fieldName.includes('rate') ||
               fieldName.includes('payment') && !fieldName.includes('setup')) {
        sections['Rates'].push(field);
      }
      // Funnel Totals
      else if (fieldName.includes('funnel') && fieldName.includes('total') ||
               fieldName.includes('total') && (fieldName.includes('applied') || fieldName.includes('qualified'))) {
        sections['Funnel Totals'].push(field);
      }
      // Funnel Stages
      else if (fieldName.includes('funnel') && fieldName.includes('stage') ||
               fieldName.includes('stage')) {
        sections['Funnel Stages'].push(field);
      }
      // Lever Requisition Actions
      else if (fieldName.includes('lever') && fieldName.includes('action')) {
        sections['Lever Requisition Actions'].push(field);
      }
      // Lever Requisition Fields
      else if (fieldName.includes('lever') && !fieldName.includes('admin') && !fieldName.includes('action')) {
        sections['Lever Requisition Fields'].push(field);
      }
      // Lever Admin
      else if (fieldName.includes('lever') && fieldName.includes('admin')) {
        sections['Lever Admin'].push(field);
      }
      // Payment Configurations
      else if (fieldName.includes('payment') && fieldName.includes('configuration') ||
               fieldName.includes('incentive') ||
               fieldName.includes('approval')) {
        sections['Payment Configurations'].push(field);
      }
      // Activation
      else if (fieldName.includes('activate') ||
               fieldName.includes('activation')) {
        sections['Activation'].push(field);
      }
      // Default to Other
      else {
        sections['Other'].push(field);
      }
    });

    // Remove empty sections
    Object.keys(sections).forEach(key => {
      if (sections[key].length === 0) {
        delete sections[key];
      }
    });

    return sections;
  };

  // Render field input based on field type
  const renderFieldInput = (field) => {
    const fieldValue = objectData?.formData[field.name] || objectData?.formData[field.name.toLowerCase().replace(/_([a-z])/g, (g) => g[1].toUpperCase())] || '';

    switch (field.type) {
      case 'boolean':
      case 'checkbox':
        return (
          <input
            type="checkbox"
            {...register(field.name)}
            defaultChecked={fieldValue === true || fieldValue === 'true'}
          />
        );
      case 'textarea':
      case 'longtextarea':
        return (
          <textarea
            {...register(field.name)}
            defaultValue={fieldValue}
            rows={4}
            style={{
              width: '100%',
              padding: '8px 12px',
              border: '1px solid #d8dde6',
              borderRadius: '6px',
              fontSize: '14px',
              fontFamily: 'inherit',
              resize: 'vertical'
            }}
          />
        );
      case 'date':
        return (
          <input
            type="date"
            {...register(field.name)}
            defaultValue={fieldValue ? new Date(fieldValue).toISOString().split('T')[0] : ''}
            style={{
              width: '100%',
              padding: '8px 12px',
              border: '1px solid #d8dde6',
              borderRadius: '6px',
              fontSize: '14px'
            }}
          />
        );
      case 'datetime':
        return (
          <input
            type="datetime-local"
            {...register(field.name)}
            defaultValue={fieldValue ? new Date(fieldValue).toISOString().slice(0, 16) : ''}
            style={{
              width: '100%',
              padding: '8px 12px',
              border: '1px solid #d8dde6',
              borderRadius: '6px',
              fontSize: '14px'
            }}
          />
        );
      default:
        return (
          <input
            type="text"
            {...register(field.name)}
            defaultValue={fieldValue}
            style={{
              width: '100%',
              padding: '8px 12px',
              border: '1px solid #d8dde6',
              borderRadius: '6px',
              fontSize: '14px'
            }}
          />
        );
    }
  };

  return (
    <div className="dashboard-layout">
      <Sidebar isOpen={sidebarOpen} toggleSidebar={() => setSidebarOpen(!sidebarOpen)} />

      <div className="project-setup" style={{ marginLeft: sidebarOpen ? '320px' : '80px', transition: 'margin-left 0.3s', width: sidebarOpen ? 'calc(100% - 320px)' : 'calc(100% - 80px)' }}>
        <div className="setup-container">
          <div className="setup-header">
            <div className="header-content">
              <div className="header-left">
                <button 
                  className="header-menu-toggle"
                  onClick={() => setSidebarOpen(!sidebarOpen)}
                  aria-label="Toggle sidebar"
                >
                  <Menu size={20} />
                </button>
                <div>
                  <h1 className="page-title">Clone</h1>
                  <p className="page-subtitle">Clone Salesforce objects</p>
                </div>
              </div>
              <div className="header-user-profile">
                <div className="user-profile">
                  <div className="user-avatar">
                    {(user?.email || 'U').charAt(0).toUpperCase()}
                  </div>
                  <span className="user-name">{user?.email || 'User'}</span>
                  <button className="logout-btn" onClick={logout} title="Logout">
                    <LogOut size={18} />
                  </button>
                </div>
              </div>
            </div>
          </div>

          <div>
          {step === 'objectType' && (
            <div>
              <h2 style={{ marginBottom: '24px', fontSize: '18px', fontWeight: 600 }}>Select Object Type to Clone</h2>
              <div style={{ maxWidth: '400px' }}>
                <label style={{ display: 'block', marginBottom: '8px', fontSize: '14px', fontWeight: 500 }}>
                  Object Type *
                </label>
                <select
                  value={selectedObjectType}
                  onChange={(e) => {
                    if (e.target.value) {
                      handleObjectTypeSelect(e.target.value);
                    }
                  }}
                  style={{
                    width: '100%',
                    padding: '12px 16px',
                    border: '1px solid #d8dde6',
                    borderRadius: '6px',
                    fontSize: '14px',
                    background: '#fff',
                    cursor: 'pointer'
                  }}
                >
                  <option value="">-- Select Object Type --</option>
                  {objectTypes.map((type) => (
                    <option key={type.value} value={type.value}>
                      {type.label}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          )}

          {step === 'search' && (
            <div>
              <div style={{ display: 'flex', alignItems: 'center', gap: '16px', marginBottom: '24px' }}>
                <button
                  onClick={() => {
                    setStep('objectType');
                    setSelectedObjectType('');
                    setSearchTerm('');
                    setSearchResults([]);
                    setSelectedObject(null);
                  }}
                  style={{
                    padding: '8px 16px',
                    border: '1px solid #d8dde6',
                    borderRadius: '6px',
                    background: '#fff',
                    cursor: 'pointer'
                  }}
                >
                  Back
                </button>
                <h2 style={{ fontSize: '18px', fontWeight: 600 }}>
                  Search {getObjectTypeLabel(selectedObjectType)}
                </h2>
              </div>

              <div style={{ position: 'relative', marginBottom: '16px' }}>
                <input
                  ref={searchInputRef}
                  type="text"
                  value={searchTerm}
                  onChange={(e) => handleSearch(e.target.value)}
                  placeholder={`Search ${getObjectTypeLabel(selectedObjectType)} by name...`}
                  style={{
                    width: '100%',
                    padding: '12px 16px',
                    paddingRight: loadingSearch ? '40px' : '16px',
                    border: '1px solid #d8dde6',
                    borderRadius: '6px',
                    fontSize: '14px'
                  }}
                />
                {loadingSearch && (
                  <div style={{ position: 'absolute', right: '12px', top: '50%', transform: 'translateY(-50%)' }}>
                    <Loader size={16} className="spinning" />
                  </div>
                )}
              </div>

              {searchResults.length > 0 && (
                <div style={{
                  border: '1px solid #d8dde6',
                  borderRadius: '6px',
                  maxHeight: '400px',
                  overflowY: 'auto'
                }}>
                  {searchResults.map((result) => (
                    <div
                      key={result.id}
                      onClick={() => handleSelectObject(result)}
                      style={{
                        padding: '12px 16px',
                        borderBottom: '1px solid #f0f0f0',
                        cursor: 'pointer',
                        transition: 'background 0.2s'
                      }}
                      onMouseEnter={(e) => e.target.style.background = '#f5f5f5'}
                      onMouseLeave={(e) => e.target.style.background = '#fff'}
                    >
                      {result.name}
                    </div>
                  ))}
                </div>
              )}

              {loadingObjectData && (
                <div style={{ textAlign: 'center', padding: '40px' }}>
                  <Loader size={24} className="spinning" />
                  <p style={{ marginTop: '16px', color: '#666' }}>Loading object data...</p>
                </div>
              )}
            </div>
          )}

          {step === 'edit' && objectData && (
            <div>
              <div style={{ display: 'flex', alignItems: 'center', gap: '16px', marginBottom: '24px' }}>
                <button
                  onClick={() => {
                    setStep('search');
                    setSelectedObject(null);
                    setObjectData(null);
                    setOriginalFormData(null);
                    setHasChanges(false);
                    reset();
                  }}
                  style={{
                    padding: '8px 16px',
                    border: '1px solid #d8dde6',
                    borderRadius: '6px',
                    background: '#fff',
                    cursor: 'pointer'
                  }}
                >
                  Back
                </button>
                <h2 style={{ fontSize: '18px', fontWeight: 600 }}>
                  Edit {getObjectTypeLabel(selectedObjectType)}: {selectedObject?.name}
                </h2>
              </div>

              <div style={{ marginBottom: '24px', padding: '16px', background: '#f5f5f5', borderRadius: '6px' }}>
                <p style={{ margin: 0, fontSize: '14px', color: '#666' }}>
                  Make changes to the fields below. When ready, click Preview to review or Publish to create the cloned object.
                </p>
              </div>

              {/* Form fields organized by sections */}
              {objectData.fields && (() => {
                const fieldSections = organizeFieldsIntoSections(objectData.fields);
                return Object.entries(fieldSections).map(([sectionName, fields]) => (
                  <div key={sectionName} className="section-content" style={{ marginBottom: '32px' }}>
                    <h2 style={{ fontSize: '18px', fontWeight: 600, marginBottom: '16px', color: '#002329' }}>
                      {sectionName}
                    </h2>
                    <div className="form-grid compact-grid">
                      {fields.map((field) => (
                        <div key={field.name} className="form-group">
                          <label style={{ fontSize: '14px', fontWeight: 500, color: '#002329' }}>
                            {field.label}
                            {field.required && <span style={{ color: '#ef4444', marginLeft: '4px' }}> *</span>}
                          </label>
                          {renderFieldInput(field)}
                          {errors[field.name] && (
                            <span className="error" style={{ fontSize: '12px', color: '#ef4444', marginTop: '4px', display: 'block' }}>
                              {errors[field.name].message || 'This field is required'}
                            </span>
                          )}
                        </div>
                      ))}
                    </div>
                  </div>
                ));
              })()}

              <div style={{ display: 'flex', gap: '12px', justifyContent: 'flex-end' }}>
                <button
                  onClick={() => {
                    const formData = watch();
                    setShowPreviewModal(true);
                  }}
                  className="btn-secondary"
                  style={{ display: 'flex', alignItems: 'center', gap: '6px' }}
                >
                  <Eye size={16} />
                  Preview
                </button>
                <button
                  onClick={handlePublish}
                  className="btn-primary"
                  disabled={publishing}
                  style={{ display: 'flex', alignItems: 'center', gap: '6px' }}
                >
                  {publishing ? (
                    <>
                      <Loader size={16} className="spinning" />
                      Publishing...
                    </>
                  ) : (
                    <>
                      <Send size={16} />
                      Publish
                    </>
                  )}
                </button>
              </div>
            </div>
          )}
          </div>
        </div>
      </div>

      {showPreviewModal && (
        <PreviewModal
          isOpen={showPreviewModal}
          onClose={() => setShowPreviewModal(false)}
          objectType={selectedObjectType}
          formData={watch()}
          objectLabel={getObjectTypeLabel(selectedObjectType)}
          onEdit={() => {
            setShowPreviewModal(false);
          }}
        />
      )}

      <ConfirmModal
        show={showNoChangesConfirm}
        message="No changes have been made to the object. The object will be cloned without any modifications. Do you want to proceed?"
        onConfirm={doPublish}
        onCancel={() => setShowNoChangesConfirm(false)}
        confirmText="Yes, Clone"
        cancelText="Cancel"
        type="info"
      />
    </div>
  );
};

export default Clone;
