import React from 'react';
import './DualProductivityCell.css';

const DualProductivityCell = ({ selfReported, systemTracked, unit = 'hours' }) => {
  const formatNumber = (num) => {
    if (num === null || num === undefined) return '0';
    return new Intl.NumberFormat('en-US').format(num);
  };

  return (
    <div className="dual-productivity-cell">
      <div className="productivity-row">
        <span className="productivity-label">Self-Reported:</span>
        <span className="productivity-value">{formatNumber(selfReported)}</span>
      </div>
      <div className="productivity-row">
        <span className="productivity-label">System Tracked:</span>
        <span className="productivity-value">{formatNumber(systemTracked)}</span>
      </div>
    </div>
  );
};

export default DualProductivityCell;
