                  formatter={(value, name, props) => [
                    `${value.toFixed(2)}%`, 
                    'Completion Rate', 
                    `Agent: ${props.payload.agent || 'Unknown'}`
                  ]}
                  labelFormatter={(label) => `Outside SLA: ${label}`}
                />
