              <tbody>
                {unresolvedSolvedByAgent.length > 0 ? unresolvedSolvedByAgent.map((row, idx) => (
                  <tr key={idx}>
                    <td>{row.agent}</td>
                    <td>{row.unresolved || '-'}</td>
