import React, { useState } from 'react';
import { ArrowLeft, Edit, Save, X } from 'lucide-react';

const CaseDetailView = ({ caseData, availableFields, onUpdate, onBack }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editedData, setEditedData] = useState(caseData);
  const [saving, setSaving] = useState(false);

  const handleFieldChange = (fieldName, value) => {
    setEditedData(prev => ({
      ...prev,
      [fieldName]: value
    }));
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      // Get only changed fields
      const updates = {};
      Object.keys(editedData).forEach(key => {
        if (editedData[key] !== caseData[key]) {
          updates[key] = editedData[key];
        }
      });

      if (Object.keys(updates).length === 0) {
        toast.info('No changes to save');
        setIsEditing(false);
        return;
      }

      await onUpdate(caseData.Id, updates);
      setIsEditing(false);
    } catch (error) {
      console.error('Error saving case:', error);
    } finally {
      setSaving(false);
    }
  };

  const handleCancel = () => {
    setEditedData(caseData);
    setIsEditing(false);
  };

  const getFieldValue = (fieldName) => {
    // Handle relationship fields
    if (fieldName.includes('.')) {
      const [relationship, field] = fieldName.split('.');
      return editedData[relationship]?.[field] || editedData[`${relationship}__r`]?.[field] || '';
    }
    return editedData[fieldName] || '';
  };

  const formatValue = (value, fieldType) => {
    if (value === null || value === undefined) return '';
    if (fieldType === 'boolean' || fieldType === 'checkbox') {
      return value ? 'Yes' : 'No';
    }
    if (fieldType === 'date' || fieldType === 'datetime') {
      return value ? new Date(value).toLocaleString() : '';
    }
    return String(value);
  };

  const renderFieldInput = (field) => {
    const value = getFieldValue(field.name);
    const fieldType = field.type;

    if (!isEditing) {
      return <span>{formatValue(value, fieldType)}</span>;
    }

    // Boolean field
    if (fieldType === 'boolean' || fieldType === 'checkbox') {
      return (
        <select
          value={value ? 'true' : 'false'}
          onChange={(e) => handleFieldChange(field.name, e.target.value === 'true')}
          className="detail-field-input"
        >
          <option value="false">No</option>
          <option value="true">Yes</option>
        </select>
      );
    }

    // Date field
    if (fieldType === 'date') {
      return (
        <input
          type="date"
          value={value ? new Date(value).toISOString().split('T')[0] : ''}
          onChange={(e) => handleFieldChange(field.name, e.target.value)}
          className="detail-field-input"
        />
      );
    }

    // DateTime field
    if (fieldType === 'datetime') {
      return (
        <input
          type="datetime-local"
          value={value ? new Date(value).toISOString().slice(0, 16) : ''}
          onChange={(e) => handleFieldChange(field.name, new Date(e.target.value).toISOString())}
          className="detail-field-input"
        />
      );
    }

    // Picklist field
    if (fieldType === 'picklist' && field.picklistValues) {
      return (
        <select
          value={value || ''}
          onChange={(e) => handleFieldChange(field.name, e.target.value)}
          className="detail-field-input"
        >
          <option value="">--Select--</option>
          {field.picklistValues.map(option => (
            <option key={option} value={option}>{option}</option>
          ))}
        </select>
      );
    }

    // Number field
    if (fieldType === 'int' || fieldType === 'double' || fieldType === 'currency' || fieldType === 'percent') {
      return (
        <input
          type="number"
          value={value || ''}
          onChange={(e) => handleFieldChange(field.name, e.target.value)}
          className="detail-field-input"
        />
      );
    }

    // Text/Textarea field
    if (fieldType === 'textarea') {
      return (
        <textarea
          value={value || ''}
          onChange={(e) => handleFieldChange(field.name, e.target.value)}
          className="detail-field-input detail-field-textarea"
          rows={4}
        />
      );
    }

    // Default text input
    return (
      <input
        type="text"
        value={value || ''}
        onChange={(e) => handleFieldChange(field.name, e.target.value)}
        className="detail-field-input"
      />
    );
  };

  // Group fields into pairs for 2-column layout
  const fieldPairs = [];
  for (let i = 0; i < availableFields.length; i += 2) {
    fieldPairs.push([
      availableFields[i],
      availableFields[i + 1]
    ]);
  }

  return (
    <div className="case-detail-view">
      <div className="case-detail-header">
        <button onClick={onBack} className="btn-back">
          <ArrowLeft size={18} />
          Back to List
        </button>
        <div className="case-detail-title">
          <h2>Case: {caseData.CaseNumber || caseData.Id}</h2>
        </div>
        <div className="case-detail-actions">
          {!isEditing ? (
            <button onClick={() => setIsEditing(true)} className="btn-edit">
              <Edit size={16} />
              Edit
            </button>
          ) : (
            <div className="edit-actions">
              <button onClick={handleCancel} className="btn-cancel">
                <X size={16} />
                Cancel
              </button>
              <button onClick={handleSave} className="btn-save" disabled={saving}>
                <Save size={16} />
                {saving ? 'Saving...' : 'Save Changes'}
              </button>
            </div>
          )}
        </div>
      </div>

      <div className="case-detail-content">
        <div className="case-detail-grid">
          {fieldPairs.map((pair, index) => (
            <React.Fragment key={index}>
              {pair[0] && (
                <div className="detail-field-group">
                  <label className="detail-field-label">{pair[0].label}:</label>
                  <div className="detail-field-value">
                    {renderFieldInput(pair[0])}
                  </div>
                </div>
              )}
              {pair[1] && (
                <div className="detail-field-group">
                  <label className="detail-field-label">{pair[1].label}:</label>
                  <div className="detail-field-value">
                    {renderFieldInput(pair[1])}
                  </div>
                </div>
              )}
            </React.Fragment>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CaseDetailView;
