/**
 * Sanitizes string input to prevent XSS attacks
 * Removes potentially dangerous HTML/JavaScript
 * @param {string} input - Input string to sanitize
 * @returns {string} - Sanitized string
 */
export const sanitizeString = input => {
  if (!input || typeof input !== 'string') return input;

  // Remove HTML tags
  let sanitized = input.replace(/<[^>]*>/g, '');

  // Remove potentially dangerous JavaScript patterns
  sanitized = sanitized.replace(/javascript:/gi, '').replace(/on\w+\s*=/gi, '').replace(/<script/gi, '').replace(/<\/script>/gi, '').replace(/<iframe/gi, '').replace(/<object/gi, '').replace(/<embed/gi, '').replace(/<link/gi, '').replace(/<style/gi, '');

  // Remove null bytes
  sanitized = sanitized.replace(/\0/g, '');
  return sanitized.trim();
};

/**
 * Sanitizes object recursively
 * @param {object} obj - Object to sanitize
 * @returns {object} - Sanitized object
 */
export const sanitizeObject = obj => {
  if (!obj || typeof obj !== 'object') return obj;
  if (Array.isArray(obj)) {
    return obj.map(item => sanitizeObject(item));
  }
  const sanitized = {};
  for (const key in obj) {
    if (obj.hasOwnProperty(key)) {
      const value = obj[key];
      if (typeof value === 'string') {
        sanitized[key] = sanitizeString(value);
      } else if (typeof value === 'object' && value !== null) {
        sanitized[key] = sanitizeObject(value);
      } else {
        sanitized[key] = value;
      }
    }
  }
  return sanitized;
};

