import React, { useState, useEffect, useImperativeHandle, forwardRef } from 'react';
import ObjectPanel from './ObjectPanel/ObjectPanel';
import QueryCanvas from './QueryCanvas/QueryCanvas';
import PreviewPanel from './PreviewPanel/PreviewPanel';
import { useQueryBuilder } from './hooks/useQueryBuilder';
import { usePreview } from './hooks/usePreview';
import '../../../styles/AdvancedBuilderCanvas.css';

const AdvancedBuilderCanvas = forwardRef((props, ref) => {
  const {
    reportConfig,
    updateConfig,
    addField,
    removeField,
    addRelationship,
    removeRelationship,
    addSubquery,
    removeSubquery,
    addFilter,
    removeFilter,
    updateSort,
    updateGroupBy
  } = useQueryBuilder();

  const {
    previewData,
    loading,
    error,
    generatePreview
  } = usePreview();

  const [previewExpanded, setPreviewExpanded] = useState(true);

  // Expose reportConfig via ref
  useImperativeHandle(ref, () => ({
    getReportConfig: () => reportConfig
  }));

  // Auto-preview when config changes (debounced)
  useEffect(() => {
    if (reportConfig.objectType && reportConfig.fields.length > 0) {
      const timer = setTimeout(() => {
        generatePreview(reportConfig);
      }, 500);
      return () => clearTimeout(timer);
    }
  }, [reportConfig, generatePreview]);

  return (
    <div className="advanced-builder-canvas">
      <div className="canvas-layout">
        {/* Left Panel - Object Browser */}
        <div className="canvas-left-panel">
          <ObjectPanel
            selectedObject={reportConfig.objectType}
            onObjectSelect={(objectType) => updateConfig({ objectType })}
            onFieldDrag={(field) => addField(field)}
            onRelationshipDrag={(relationship) => addRelationship(relationship)}
            onSubqueryDrag={(subquery) => addSubquery(subquery)}
          />
        </div>

        {/* Center Panel - Visual Query Builder */}
        <div className="canvas-center-panel">
          <QueryCanvas
            reportConfig={reportConfig}
            onFieldAdd={addField}
            onFieldRemove={removeField}
            onRelationshipAdd={addRelationship}
            onRelationshipRemove={removeRelationship}
            onSubqueryAdd={addSubquery}
            onSubqueryRemove={removeSubquery}
            onFilterAdd={addFilter}
            onFilterRemove={removeFilter}
            onSortUpdate={updateSort}
            onGroupByUpdate={updateGroupBy}
            onConfigUpdate={updateConfig}
          />
        </div>
      </div>

      {/* Bottom Panel - Preview */}
      <PreviewPanel
        data={previewData}
        loading={loading}
        error={error}
        expanded={previewExpanded}
        onToggle={() => setPreviewExpanded(!previewExpanded)}
        reportName={reportConfig.name}
      />
    </div>
  );
});

AdvancedBuilderCanvas.displayName = 'AdvancedBuilderCanvas';

export default AdvancedBuilderCanvas;
