import React, { useState } from 'react';
import { X, Plus, Package } from 'lucide-react';
import FieldChip from '../FieldChip';
import InfoModal from '../../../InfoModal';
import '../../../../styles/ObjectCard.css';

const ObjectCard = ({ objectType, fields, onFieldAdd, onFieldRemove }) => {
  const [showInfoModal, setShowInfoModal] = useState(false);
  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    try {
      const data = JSON.parse(e.dataTransfer.getData('application/json'));
      if (data.type === 'field' && !data.field.name.includes('.')) {
        onFieldAdd(data.field);
      }
    } catch (error) {
      console.error('Error handling drop:', error);
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
    e.dataTransfer.dropEffect = 'move';
  };

  return (
    <div className="query-card object-card">
      <div className="card-header">
        <div className="card-title">
          <Package size={18} />
          <span>{objectType || 'Select Object'}</span>
        </div>
      </div>

      <div
        className="card-content"
        onDrop={handleDrop}
        onDragOver={handleDragOver}
      >
        {fields.length === 0 ? (
          <div className="card-empty">
            <p>Drag fields here or click + Add Field</p>
            <button
              className="add-field-btn"
              onClick={() => {
                setShowInfoModal(true);
              }}
            >
              <Plus size={14} /> Add Field
            </button>
          </div>
        ) : (
          <div className="card-fields">
            {fields.map(field => {
              const fieldName = typeof field === 'string' ? field : field.name;
              const fieldLabel = typeof field === 'string' ? field : (field.label || field.name);
              return (
                <FieldChip
                  key={fieldName}
                  label={fieldLabel}
                  name={fieldName}
                  onRemove={() => onFieldRemove(fieldName)}
                />
              );
            })}
            <button
              className="add-field-btn-small"
              onClick={() => {
                setShowInfoModal(true);
              }}
            >
              <Plus size={12} /> Add Field
            </button>
          </div>
        )}
      </div>

      {/* Info Modal */}
      <InfoModal
        show={showInfoModal}
        message="Use the left panel to drag fields here, or select fields from the object browser."
        onClose={() => setShowInfoModal(false)}
        title="How to Add Fields"
      />
    </div>
  );
};

export default ObjectCard;
